import { get, post } from "../../api/axios.js";
import gjszApproval from "./gjszApproval";
export default {
  components: {
    gjszApproval: gjszApproval
  },
  data: function data() {
    return {
      tiao: false,
      arrdata: [],
      all: 100,
      //      总页数
      cur: 1,
      multipleSelection: []
    };
  },
  mounted: function mounted() {
    this.shua();
  },
  methods: {
    shua: function shua() {
      var that = this;
      get('/alarmset/selectSetAll', {
        cpid: this.$store.state.user.companyId,
        pageNumber: that.cur - 1,
        pageSize: 8
      }).then(function (res) {
        for (var i = 0; i < res.content.length; i++) {
          res.content[i].baoc = false;
        }

        that.arrdata = res.content;
        that.all = res.totalElements;
      });
    },
    handleSelectionChange: function handleSelectionChange(val) {
      this.multipleSelection = val;
    },
    btnClick: function btnClick(data) {
      this.cur = data;
      this.shua();
    },
    opfa: function opfa(i) {
      var _this = this;

      if (!this.tiao) {
        if (this.multipleSelection.length >= 1) {
          var data = [];

          for (var o = 0; o < this.multipleSelection.length; o++) {
            data.push({
              id: this.multipleSelection[o].id,
              auditState: i + ''
            });
          }

          console.log(data);
          post('/alarmset/batchUpdateSetState', data).then(function (res) {
            _this.shua();
          });
        }
      } else {
        this.$refs.tiaodiv.opfa(i);
      }
    },
    shfa: function shfa(i) {
      console.log(i);
    }
  }
};
