import { get, post } from "../../api/axios.js";
export default {
  data: function data() {
    return {
      $index: '',
      name: '',
      ip2: '',
      arrdata: [],
      multipleTable: [],
      arr: [],
      all: 0,
      //      总页数
      cur: 1,
      data: {},
      multipleSelection: [],
      xt: '',
      zhdata: {},
      zh: false,
      xgdata: {},
      xg: false,
      pickerOptions2: {
        shortcuts: [{
          text: '最近一周',
          onClick: function onClick(picker) {
            var end = new Date();
            var start = new Date();
            start.setTime(start.getTime() - 3600 * 1000 * 24 * 7);
            picker.$emit('pick', [start, end]);
          }
        }, {
          text: '最近一个月',
          onClick: function onClick(picker) {
            var end = new Date();
            var start = new Date();
            start.setTime(start.getTime() - 3600 * 1000 * 24 * 30);
            picker.$emit('pick', [start, end]);
          }
        }, {
          text: '最近三个月',
          onClick: function onClick(picker) {
            var end = new Date();
            var start = new Date();
            start.setTime(start.getTime() - 3600 * 1000 * 24 * 90);
            picker.$emit('pick', [start, end]);
          }
        }]
      },
      shij: [],
      value4: '',
      options2: [],
      options: [{
        value: '选项1',
        label: '全部'
      }, {
        value: '选项2',
        label: '主动报警'
      }, {
        value: '选项3',
        label: '触发报警'
      }, {
        value: '选项4',
        label: '异常报警'
      }, {
        value: '选项5',
        label: '断电报警'
      }, {
        value: '选项6',
        label: '超速报警'
      }, {
        value: '选项7',
        label: '越界报警'
      }, {
        value: '选项8',
        label: '超时报警'
      }, {
        value: '选项9',
        label: '求助报警'
      }],
      value5: [],
      value11: [],
      cities: [{
        value1: 'haikang',
        label: '垃圾桶'
      }, {
        value1: 'dahua',
        label: '密度摄像机'
      }, {
        value1: 'huawei',
        label: '客流相机'
      }],
      value6: '',
      value10: [],
      zhfadata: {}
    };
  },
  mounted: function mounted() {
    var _this = this;

    this.shua();
    get('/alarmInfo/selectCountAlarmSource', {
      cpid: this.$store.state.user.companyId
    }).then(function (response) {
      _this.options2 = response;
    });
    get('/alarmInfo/selectCountAlarmType', {
      cpid: this.$store.state.user.companyId
    }).then(function (res) {
      _this.options = res;
    });
  },
  methods: {
    clickok: function clickok() {
      this.zh = false;
      this.zhdata = [];
      this.xg = false;
    },
    zhfa: function zhfa(i) {
      this.zh = true;
      this.zhfadata = i;
    },
    xgfa: function xgfa(i) {
      console.log(i);
      this.xg = true;
      var that = this;
      get('/alarmInfo/selectById', {
        aid: i.id
      }).then(function (res) {
        console.log(res);
        that.xgdata = res[0];
      });
    },
    sou: function sou() {
      var that = this;
      var data = {
        cpid: this.$store.state.user.companyId,
        pageNumber: that.cur - 1,
        pageSize: 6,
        alarmSource: this.data.xt,
        alarmType: this.data.alarmType,
        handlerName: this.data.name
      };
      console.log(data);

      if (that.shij.length > 0) {
        console.log(that.shij);
        data.startTime = this.shi2(that.shij[0]);
        data.endTime = this.shi2(that.shij[1]);
      }

      get('/alarmInfo/selectByClear', data).then(function (res) {
        that.arrdata = res.content;
        that.all = res.totalElements;
        console.log(res, 100);
      });
    },
    handleSelectionChange: function handleSelectionChange(val) {
      this.multipleSelection = val;
    },
    btnClick: function btnClick(data) {
      console.log(data);
      this.cur = data;
      this.shua();
    },
    shua: function shua() {
      var _this2 = this;

      var that = this;
      get('/alarmInfo/selectAllPage', {
        cpid: this.$store.state.user.companyId,
        pageNumber: that.cur - 1,
        pageSize: 6
      }).then(function (res) {
        that.arrdata = res.content;
        _this2.all = res.totalElements;
        console.log(res, 12212);
      });
    },
    shi: function shi(value) {
      var s = value / 1000;
      var m = parseInt(s % 60);
      s = parseInt(s / 60);
      var f = parseInt(s % 60);
      s = parseInt(s / 60);
      var date = (s < 10 ? '0' + s : s) + ':' + (f < 10 ? '0' + f : f) + ':' + (m < 10 ? '0' + m : m);
      return date;
    },
    shi2: function shi2(date) {
      var n = date.getFullYear();
      var y = date.getMonth() + 1;
      var r = date.getDate();
      var s = date.getHours() < 10 ? '0' + date.getHours() : date.getHours();
      var f = date.getMinutes() < 10 ? '0' + date.getMinutes() : date.getMinutes();
      var m = date.getSeconds() < 10 ? '0' + date.getSeconds() : date.getSeconds();
      return n + '-' + y + '-' + r + ' ' + s + ':' + f + ':' + m;
    },
    content: function content() {
      var _this3 = this;

      post('/alarmInfo/saveAlarmContent', {
        aid: this.zhfadata.id,
        alarmContent: this.data.xr
      }).then(function (res) {
        _this3.shua();

        _this3.clickok();

        console.log(res, 12212);
      });
    }
  }
};
