export default {
  name: 'DataWapper',
  props: {
    wapperType: {
      // 页面类型1添加 0修改
      type: Number,
      default: 0
    },
    childSysList: {
      // 子系统列表
      type: Array,
      default: function _default() {
        return [];
      }
    },
    childSysText: {
      // 子系统名称
      type: String,
      default: ''
    }
  },
  data: function data() {
    return {
      collectionForm: {
        id: 0,
        name: '',
        company: '',
        childSys: this.childSysText,
        childId: 1,
        server: '',
        username: '',
        password: '',
        protocol: 'HTTP',
        coding: '',
        remark: ''
      },
      collectionRules: {
        name: [{
          required: true,
          message: '请输入数据采集名称',
          trigger: 'blur'
        }],
        company: [{
          required: true,
          message: '请输入厂商名称',
          trigger: 'blur'
        }],
        server: [{
          required: true,
          message: '请输入服务器名称或IP',
          trigger: 'blur'
        }],
        username: [{
          required: true,
          message: '请输入登录用户名',
          trigger: 'blur'
        }],
        password: [{
          required: true,
          message: '请输入密码',
          trigger: 'blur'
        }],
        coding: [{
          required: true,
          message: '请输入内容编码',
          trigger: 'blur'
        }]
      },
      protocolList: [// 协议类型
      {
        val: 1,
        name: 'HTTP'
      }, {
        val: 2,
        name: 'HTTPS'
      }, {
        val: 3,
        name: '串口'
      }, {
        val: 4,
        name: 'UDP'
      }],
      btnSave: false,
      loading: false
    };
  },
  methods: {
    handleChildDD: function handleChildDD(item) {
      // 子系统下拉选择
      this.collectionForm.childSys = item.name;
      this.collectionForm.childId = item.id;
    },
    handleDD: function handleDD(item) {
      // 协议下拉选择
      this.collectionForm.protocol = item.name;
    },
    updateStatus: function updateStatus() {
      if (this.btnSave) {
        return false;
      } else if (this.collectionForm.id <= 0) {
        return false;
      } else {
        return true;
      }
    },
    handleCancel: function handleCancel() {
      this.$emit('cancel');
    },
    handleSave: function handleSave() {
      var _this = this;

      // 保存
      this.$refs.collectionForm.validate(function (valid) {
        if (valid) {
          _this.loading = true;

          if (_this.wapperType === 1) {
            _this.$emit('add', _this.collectionForm);
          } else {
            _this.$emit('update', _this.collectionForm);
          }
        }
      });
    }
  }
};
