export default {
  name: 'Tourist',
  data: function data() {
    return {
      default_bar: 0,
      name: '',
      messages_tart: '',
      messages_content: '',
      messages_end: '',
      isused: true,
      checkedCities: [],
      cities: ['短信', '手机游景区', '手机管景区'],
      isshow: false,
      disabled: true,
      edit: false,
      list_value: [{
        name: '游前温馨提示0',
        value: false
      }, {
        name: '游前温馨提示1',
        value: false
      }, {
        name: '游前温馨提示2',
        value: false
      }, {
        name: '游前温馨提示3',
        value: false
      }, {
        name: '游前温馨提示4',
        value: false
      }, {
        name: '游前温馨提示5',
        value: false
      }]
    };
  },
  computed: {
    datas: function datas() {
      if (this.list_value.length > 0) {
        return false;
      } else {
        return true;
      }
    },
    save: function save() {
      return this.name && this.messages_tart && this.messages_content && this.messages_end && this.checkedCities.length > 0;
    }
  },
  watch: {},
  mounted: function mounted() {
    // this.list_value = []
    this.Clickli(0);
  },
  methods: {
    addClick: function addClick() {
      var _this = this;

      var that = this;
      this.$prompt('请输入消息模块名称', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        inputPattern: /\S{3,}/,
        inputErrorMessage: '请检查消息模块名称是否正确(不少于三个任意字符)'
      }).then(function (_ref) {
        var value = _ref.value;

        // this.$message({
        //   type: 'success',
        //   message: '你的邮箱是: ' + value
        // })
        if (value) {
          that.list_value.push({
            name: value,
            value: false
          });
          that.Clickli(that.list_value.length - 1);
        }
      }).catch(function () {
        _this.$message({
          type: 'info',
          message: '取消输入'
        });
      });
    },
    delClick: function delClick() {
      var _this2 = this;

      var that = this;
      var val = that.list_value[that.default_bar].name;
      this.$confirm('<span>是否删除:&nbsp;&nbsp;<span style="color: red;">' + val + '</span></span>', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'error',
        dangerouslyUseHTMLString: true,
        center: true
      }).then(function () {
        if (that.default_bar || that.default_bar === 0) {
          that.list_value.splice(that.default_bar, 1);
          that.default_bar = null;
        }
      }).catch(function () {
        _this2.$message({
          type: 'message',
          message: '已取消删除'
        });
      });
    },
    changeClick: function changeClick() {
      this.isshow = false;
      this.disabled = false; // this.edit = true

      console.log('当前修改的是' + this.default_bar);
    },
    saveClick: function saveClick() {
      this.edit = false;
      this.disabled = true;
      console.log('当前保存的是' + this.default_bar);
      console.log(this.checkedCities);
    },
    Clickli: function Clickli(index) {
      // window.event.stopPropagation()
      this.default_bar = index;
      var that = this;

      for (var i = 0; i < that.list_value.length; i++) {
        if (i === index) {
          that.list_value[i].value = !that.list_value[i].value;
        } else {
          that.list_value[i].value = false;
        }
      }
    },
    switchClick: function switchClick(index) {
      var that = this;
      that.default_bar = index;
      window.event.stopPropagation();

      for (var i = 0; i < that.list_value.length; i++) {
        if (i !== index) {
          that.list_value[i].value = false;
        }
      }
    }
  }
};
