import { get, post } from "../../api/axios.js";
export default {
  name: 'News',
  props: {
    estdata: {
      type: Object,
      default: function _default() {
        return {};
      }
    }
  },
  data: function data() {
    return {
      tab: '1',
      arrdata: [{
        jmxh: 'CB000001',
        jmmc: '新增插播节目1',
        fbl: '1920*1080',
        kssj: '2019-04-26 10:00:00',
        zzsj: '2019-04-26 10:10:00',
        cs: 3,
        cjry: '朱清',
        bz: ''
      }],
      options: [{
        value: '1',
        label: '按时间播放'
      }, {
        value: '2',
        label: '按次数播放'
      }],
      value: '1',
      value1: [],
      value2: 0,
      options2: []
    };
  },
  watch: {
    tab: function tab() {
      console.log(this.tab);
    }
  },
  created: function created() {
    var _this = this;

    // console.log(this.$route.query.data)
    console.log(this.estdata);
    get('/camara/getEqType2?', {
      system_id: 3
    }).then(function (res) {
      console.log(res);
      _this.options2 = res;
    });
  },
  methods: {
    handleOpen: function handleOpen(key, keyPath) {
      console.log(key, keyPath);
      console.log(this.tab);
    },
    handleClose: function handleClose(key, keyPath) {
      console.log(key, keyPath); // this.tab = 0
    },
    fang: function fang(index, inde) {
      this.tab = index;
      console.log(this.tab, index);
    },
    baocun: function baocun() {
      var data = this.estdata;
      data.showStartTime = this.date(new Date()) + ' ' + this.value1[0] + ':' + '00';
      data.showEndTime = this.date(new Date()) + ' ' + this.value1[1] + ':' + '00';
      data.equipmentNo = this.options2[this.tab * 1].id;
      data.equipmentName = this.options2[this.tab * 1].name;
      data.ipAddress = '196.128.1.165';
      this.$router.push({
        path: '/news/index'
      });
      console.log(data);
      post('/RadioProgramme/saveLedProgrammeList', data).then(function (res) {
        console.log(res.data); // that.arrdata = res.data.content
      });
    },
    date: function date(_date) {
      var d = new Date(_date);
      var resDate = d.getFullYear() + '-' + this.p(d.getMonth() + 1) + '-' + this.p(d.getDate()); // const resTime = this.p(d.getHours()) + ':' + this.p(d.getMinutes()) + ':' + this.p(d.getSeconds())

      return resDate;
    },
    p: function p(s) {
      return s < 10 ? '0' + s : s;
    }
  }
};
