// import { get } from '@/api/axios'
export default {
  name: 'Tourist',
  data: function data() {
    return {
      addnew: false,
      // 当前新建状态
      editnew: false,
      // 当前编辑状态
      list: '',
      // 编辑时弹框的内容数据
      index_num: null,
      // 编辑或者删除时第几项数据
      currentPage4: 1,
      // 分页默认的前往页
      radio: '1',
      radios: '1',
      dialogFormVisible: false,
      // 弹框是否显示
      formLabelWidth: '120px',
      // 弹框宽度
      titles: '增加营销',
      show_box: {
        // 弹框内的数据
        nameTitle: '',
        type: '',
        name: '',
        module: '',
        // 模板
        dataTime: '',
        pushTime: '',
        pushPerson: '',
        isused: false
      },
      pageShow: false,
      // 是否显示分页
      datashow: false,
      listStyle: [],
      options: [{
        value: '类型1',
        label: '类型1'
      }, {
        value: '类型2',
        label: '类型2'
      }, {
        value: '类型3',
        label: '类型3'
      }],
      options1: [{
        value: '青少年',
        label: '青少年'
      }, {
        value: '中年',
        label: '中年'
      }, {
        value: '老年',
        label: '老年'
      }]
    };
  },
  computed: {
    save: function save() {
      return this.show_box.type && this.show_box.name && this.show_box.dataTime && this.show_box.pushTime && this.show_box.pushPerson;
    }
  },
  watch: {},
  mounted: function mounted() {
    this.listStyle = [// 默认加载的所有数据集合
    {
      nameTitle: '营销1号',
      isused: false,
      type: '类型1',
      name: '营销1号',
      dataTime: '2019-12-01',
      pushTime: '2012-12-12',
      pushPerson: '青年',
      textarea: '息模板消息消息板消息消息模板消息消息模板消息消息'
    }, {
      nameTitle: '营销2号',
      isused: true,
      type: '类型2',
      name: '营销2号',
      dataTime: '2019-12-01',
      pushTime: '2012-12-12',
      pushPerson: '青年',
      textarea: '息模板消息消息板消息消息模板消息消息模板消息消息'
    }];

    if (this.listStyle.length > 2) {
      this.pageShow = true;
    } else {
      this.pageShow = false;

      if (this.listStyle.length === undefined || this.listStyle.length === 0) {
        this.datashow = true;
      } else {
        this.datashow = false;
      }
    }
  },
  methods: {
    add: function add() {
      this.dialogFormVisible = true;
      this.addnew = true;
      this.editnew = false;
      this.titles = '增加营销';
      this.show_box.nameTitle = null;
      this.show_box.type = null;
      this.show_box.name = null;
      this.show_box.module = null;
      this.show_box.dataTime = null;
      this.show_box.pushTime = null;
      this.show_box.pushPerson = null;
      this.show_box.isused = null;
    },
    // 弹框确定按钮
    handleBoxSave: function handleBoxSave() {
      this.dialogFormVisible = false;
      this.listStyle[this.index_num] = this.list;
    },
    // 弹框取消按钮
    // handleBoxCancel() {
    //   this.dialogFormVisible = false
    // },
    editClick: function editClick(index) {
      this.dialogFormVisible = true;
      this.titles = '增加营销';
      this.index_num = index;
      this.addnew = false;
      this.editnew = true;
      this.list = JSON.parse(JSON.stringify(this.listStyle[index])); // 数据深拷贝

      this.show_box = this.list;
      this.titles = this.list.nameTitle;
    },
    delClick: function delClick(index) {
      var _this = this;

      var that = this;
      var name = that.listStyle[index].name;
      this.$confirm('<span>是否删除:&nbsp;&nbsp;<span style="color: red;">' + name + '</span></span>', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'error',
        dangerouslyUseHTMLString: true
      }).then(function () {
        // this.$message({
        //   type: 'success',
        //   message: '删除成功!'
        // })
        that.listStyle.splice(index, 1);

        if (that.listStyle.length > 0) {
          that.pageShow = true;
        } else {
          that.pageShow = false;
        }
      }).catch(function () {
        _this.$message({
          type: 'info',
          message: '已取消删除'
        });
      });
    },
    saveClick: function saveClick() {
      console.log(this.show_box);
      this.show_box.nameTitle = this.show_box.name;
      var data = this.show_box;
      this.listStyle.push(data);
    },
    cancelClick: function cancelClick() {},
    handleSizeChange: function handleSizeChange(val) {
      console.log("\u6BCF\u9875".concat(val, " \u6761"));
    },
    handleCurrentChange: function handleCurrentChange(val) {
      console.log("\u5F53\u524D\u9875:".concat(val));
    }
  }
};
