import { get } from "../../api/axios.js";
export default {
  name: 'About',
  data: function data() {
    return {
      pagesize: 3,
      pageNumber: 2,
      all: 0,
      //      总页数
      cur: 1,
      arrdata: [],
      shij: [],
      shija: [],
      arr: [],
      data: {},
      multipleSelection: []
    };
  },
  computed: {},
  watch: {},
  mounted: function mounted() {
    this.shua();
    var that = this;
    get('/maintain/getmaintain', {
      cpid: this.$store.state.user.companyId,
      pageNumber: that.cur - 1,
      pageSize: 8
    }).then(function (res) {
      that.arrdata = res.content;
      that.all = res.totalElements;
      console.log(res, 1112);
    });
  },
  methods: {
    handleSelectionChange: function handleSelectionChange(val) {
      this.multipleSelection = val;
    },
    btnClick: function btnClick(data) {
      console.log(data);
      this.cur = data;
      this.shua();
    },
    shua: function shua() {
      console.log(this.cur, this.arrdata);
      this.arrdata = [];

      for (var i = 0; i < 10; i++) {
        if (i < this.arr.length && this.arr[i + this.cur * 8 - 8]) {
          this.arrdata.push(this.arr[i + this.cur * 8 - 8]);
        }
      }
    },
    shi: function shi(value) {
      var s = value / 1000;
      var m = parseInt(s % 60);
      s = parseInt(s / 60);
      var f = parseInt(s % 60);
      s = parseInt(s / 60);
      var date = (s < 10 ? '0' + s : s) + ':' + (f < 10 ? '0' + f : f) + ':' + (m < 10 ? '0' + m : m);
      return date;
    },
    shi2: function shi2(date) {
      var n = date.getFullYear();
      var y = date.getMonth() + 1;
      var r = date.getDate();
      var s = date.getHours() < 10 ? '0' + date.getHours() : date.getHours();
      var f = date.getMinutes() < 10 ? '0' + date.getMinutes() : date.getMinutes();
      var m = date.getSeconds() < 10 ? '0' + date.getSeconds() : date.getSeconds();
      return n + '-' + y + '-' + r + ' ' + s + ':' + f + ':' + m;
    },
    sou: function sou() {
      var that = this;
      var data = {
        cpid: this.$store.state.user.companyId,
        pageNumber: that.cur - 1,
        pageSize: 8,
        alarmName: this.data.sbmc,
        alarmAddress: this.data.sbwz,
        repairman: this.data.wxfzr
      };

      if (that.shij.length > 0) {
        console.log(that.shij);
        data.startTime = this.shi2(that.shij[0]);
        data.endTime = this.shi2(that.shij[1]);
      }

      if (that.shija.length > 0) {
        data.startTime2 = this.shi2(that.shija[0]);
        data.endTime2 = this.shi2(that.shija[1]);
      }

      get('/maintain/getmaintain', data).then(function (res) {
        that.arrdata = res.content;
        that.all = res.totalElements;
        console.log(res, 100);
      });
    }
  }
};
