    clickLi: function clickLi(val) {
      this.active = val;
      this.map.clearMap(); // 清除地图覆盖物

      this.markers = [];
      this.marker = [];
      this.line = [];
      this.changeMarkers(val);
    },
    changeMarkers: function changeMarkers(val) {
      var that = this; // 驿站    post videolook positioning runway waterlook

      var marker1 = [[100.397094, 25.944405]]; // 视频监控 videolook

      var marker2 = [[100.412715, 25.944227], [100.405162, 25.930489]]; // 人员定位 positioning

      var marker3 = [[100.405162, 25.930489], [100.424731, 25.929253], [100.383189, 25.923078]]; // 智能跑道marker2 --runway

      var marker4 = [[100.162261, 25.6953135], [100.181144, 25.690804], [100.123122, 25.672549]]; // 水质监测 waterlook

      var marker5 = [[100.397094, 25.944405], [100.412715, 25.944227], [100.405162, 25.930489], [100.424731, 25.929253], [100.383189, 25.923078]];

      if (val === '驿站') {
        that.marker = marker1;
        val = that.post;
      } else if (val === '视频监控') {
        that.marker = marker2;
        val = that.videolook;
      } else if (val === '人员定位') {
        that.marker = marker3;
        val = that.positioning;
      } else if (val === '水质监测') {
        that.marker = marker5;
        val = that.waterlook;
      } else {
        that.line = marker4;
      }

      for (var i = 0; i < that.marker.length; i++) {
        that.markers.push(new AMap.Marker({
          icon: val,
          position: that.marker[i]
        }));
      }

      if (this.active === '智能跑道') {
        that.polyline = new AMap.Polyline({
          path: that.line,
          isOutline: true,
          outlineColor: '#ffeeff',
          borderWeight: 3,
          strokeColor: '#3366FF',
          strokeOpacity: 1,
          strokeWeight: 6,
          // 折线样式还支持 'dashed'
          strokeStyle: 'solid',
          // strokeStyle是dashed时有效
          strokeDasharray: [10, 5],
          lineJoin: 'round',
          lineCap: 'round',
          zIndex: 50
        });
        that.polyline.setMap(that.map); // 缩放地图到合适的视野级别

        that.map.setFitView([that.polyline]);
      } else {
        that.map.add(that.markers);
        that.map.setFitView();
      }
    },
