import { post, get } from '@/api/axios';
export default {
  name: 'Broadcast',
  data: function data() {
    return {
      fileList: [],
      // 分页
      lx: '',
      bc: '',
      zdl: '',
      mes: '',
      pagesize: 1,
      pageNumber: 10,
      arrdata: [],
      fudata: {
        cpid: this.$store.state.user.companyId
      },
      //    分页
      all: 0,
      //      总页数
      cur: 1,
      mingc: '',
      ip: '',
      type: '',
      data: {},
      multipleSelection: []
    };
  },
  computed: {},
  watch: {},
  mounted: function mounted() {
    this.shua();
  },
  methods: {
    handleSelectionChange: function handleSelectionChange(val) {
      this.multipleSelection = val;
    },
    btnClick: function btnClick(data) {
      console.log(data);
      this.cur = data;
      this.shua();
    },
    shua: function shua() {
      var that = this;
      get('/mngradio/getradiofile', {
        cpid: this.$store.state.user.companyId,
        pageNumber: that.cur - 1,
        pageSize: 10
      }).then(function (res) {
        console.log(123, res);
        that.arrdata = res.data.content;
        that.all = res.data.numberOfElements;
      });
    },
    updata: function updata(data) {
      var _this = this;

      var formData = new FormData();
      formData.append('file', data);
      post('/mngradio/saveradiofile/' + this.$store.state.user.companyId, formData).then(function (res) {
        console.log(res);

        _this.shua();
      });
      return false;
    },
    delet: function delet() {
      var _this2 = this;

      var that = this;
      var ids = '';

      for (var i = 0; i < that.multipleSelection.length; i++) {
        if (i === 0) {
          ids += that.multipleSelection[i].id;
        } else {
          ids += ',' + that.multipleSelection[i].id;
        }
      }

      get('/mngradio/delradiofile', {
        ids: ids
      }).then(function (res) {
        console.log(res.msg);

        if (res.msg === 'SUCCESS') {
          _this2.shua();
        } else {
          _this2.$message({
            message: res.msg,
            type: 'warning'
          });
        }
      });
    },
    alldelet: function alldelet() {
      var _this3 = this;

      var ids = '';
      var that = this;

      for (var i = 0; i < that.arrdata.length; i++) {
        if (i === 0) {
          ids += that.arrdata[i].id;
        } else {
          ids += ',' + that.arrdata[i].id;
        }
      }

      get('/mngradio/delradiofile', {
        ids: ids
      }).then(function (res) {
        console.log(res);

        _this3.shua();
      });
    }
  }
};
