import VueAMap from 'vue-amap';
var amapManager = new VueAMap.AMapManager();
export default {
  name: 'Map',
  data: function data() {
    return {
      name: '',
      bei: '',
      textarea: '',
      zoom: 16,
      center: [116.336496, 39.942178],
      mapStyle1: 'amap://styles/whitesmoke',
      activeName: 'qing',
      tape: 1,
      amapManager: amapManager,
      events: {},
      overlayGroups: [],
      data: [],
      diandata: {
