  },
  watch: {
    data: function data() {// this.addmarker()
    }
  },
  created: function created() {
    this.addfun();
  },
  methods: {
    ti: function ti() {
      var _this = this;

      if (this.name !== '' && this.value !== '') {
        this.$message({
          message: '保存成功',
          type: 'success'
        });
        setTimeout(function () {
          _this.tank = '';

          _this.$router.push({
            path: '/maps/linjingxi'
          });
        }, 2000);
      } else {
        this.$message({
          message: '请认真检查填写项',
          type: 'warning'
        });
      }
    },
    ti2: function ti2() {
      if (this.name !== '' && this.value !== '') {
        this.name = '';
        this.bei = '';
        this.value = '';
        this.diandata = {
          a: 0,
          b: 0
        };
        this.overlayGroups = [];
        this.addmarker();
        this.$message({
          message: '保存成功',
          type: 'success'
        });
      } else {
        this.$message({
          message: '请认真检查填写项',
          type: 'warning'
        });
      }
    },
    addfun: function addfun() {
      var that = this;
      this.events = {
        init: function init(o) {
          o.on('click', function (e) {
            that.wei(e.lnglat.getLng(), e.lnglat.getLat(), that);
          });
        }
      };
    },
    addmarker: function addmarker() {
      var that = this;
      var o = amapManager.getMap();
      var markers = [];
      o.remove(that.overlayGroups);
      console.log(2);
      var marker = new AMap.Marker({
        position: [that.diandata.a, that.diandata.b],
        text: '123'
      });
      markers.push(marker);
      this.overlayGroups = new AMap.OverlayGroup(markers);
      o.add(that.overlayGroups);
    },
    wei: function wei(a, b, that) {
      this.data[this.data.length - 1] = [a, b];
      that.diandata.a = a;
      that.diandata.b = b;
      this.addmarker();
    },
    handleClick: function handleClick(tab, event) {
      console.log(tab, event);
    }
  }
};
