export default {
  name: 'Tourist',
  data: function data() {
    return {
      radio: null,
      input_times: '',
      input_nums: '',
      disabled: true,
      edit: false,
      save: true,
      times: true,
      nums: true
    };
  },
  computed: {// selects: function() {
    //   return (this.radio && this.input_times && this.input_nums)
    // }
  },
  watch: {},
  mounted: function mounted() {},
  methods: {
    editClick: function editClick() {
      this.disabled = false;
      this.edit = true;
      this.save = false;
    },
    saveClick: function saveClick() {
      this.disabled = true;
      this.edit = false;
      this.save = true;
    },
    radioClick: function radioClick(val) {
      var that = this;

      if (parseInt(val) === 1) {
        that.times = false;
        that.nums = true;
      } else if (parseInt(val) === 2) {
        that.nums = false;
        that.times = true;
      }
    }
  }
};
