export default {
  name: 'Fabuy',
  components: {},
  data: function data() {
    return {
      // div的大小位置
      left: 0,
      top: 0,
      width: 100,
      height: 100,
      dataname: '',
      taid: '',
      // 判断有没有点击
      whtf: false,
      lttf: false,
      // 记录鼠标位置
      shux: false,
      shuy: false,
      shux2: false,
      shuy2: false,
      data: [],
      ddata: null
    };
  },
  computed: {},
  watch: {
    left: function left() {
      this.divup();
    },
    top: function top() {
      this.divup();
    },
    width: function width() {
      this.divup();
    },
    height: function height() {
      this.divup();
    }
  },
  mounted: function mounted() {
    this.divup();
  },
  methods: {
    // 鼠标点下去记录鼠标当前位置
    down: function down(data) {
      this.whtf = true;
      this.shux = data.clientX - this.left;
      this.shuy = data.clientY - this.top;
    },
    down2: function down2(data) {
      this.lttf = true;
      this.shux2 = data.clientX - this.width;
      this.shuy2 = data.clientY - this.height;
    },
    // 鼠标松开清空位置信息
    up: function up() {
      this.whtf = false;
      this.shux = false;
      this.shuy = false;
      this.lttf = false;
      this.shux2 = false;
      this.shuy2 = false;
      this.data[this.ddata].pointw = this.width;
      this.data[this.ddata].pointh = this.height;
      this.data[this.ddata].pointx = this.left;
      this.data[this.ddata].pointy = this.top;
    },
    // 移动鼠标改变位置
    move: function move(data) {
      if (this.whtf && !this.lttf) {
        this.left = data.clientX - this.shux;
        this.top = data.clientY - this.shuy;
      }
    },
    move2: function move2(data) {
      if (this.lttf) {
        this.width = data.clientX - this.shux2;
        this.height = data.clientY - this.shuy2;
      }
    },
    // 刷新宽高和位置
    divup: function divup() {
      this.$refs.zhu.style.height = this.height + 'px';
      this.$refs.zhu.style.width = this.width + 'px';
      this.$refs.zhu.style.marginTop = this.top + 'px';
      this.$refs.zhu.style.marginLeft = this.left + 'px';
    },
    // 添加文件
    add: function add(data) {
      var dta = data.target.files[0];
      dta.pointw = 100;
      dta.pointy = 0;
      dta.pointx = 0;
      dta.pointh = 100;
      this.data.push(dta);
      console.log(this.data);
      this.whxy(dta.pointw, dta.pointh, dta.pointx, dta.pointy, dta.name, this.data.length - 1);
    },
    shan: function shan() {
      this.data.splice(this.ddata, 1);
      this.width = '';
      this.height = '';
      this.left = '';
      this.top = '';
      this.dataname = '';
      this.ddata = '';
    },
    dataup: function dataup() {
      if (this.ddata > 0) {
        this.data.splice(this.ddata - 1, 0, this.data[this.ddata]);
        this.data.splice(this.ddata + 1, 1);
        this.ddata = this.ddata - 1;
      } else {
        alert(this.ddata);
      }
    },
    datadown: function datadown() {
      if (this.ddata < this.data.length) {
        this.data.splice(this.ddata + 2, 0, this.data[this.ddata]);
        this.data.splice(this.ddata, 1);
        this.ddata = this.ddata + 1;
      } else {
        alert(this.ddata);
      }
    },
    // 改变div
    whxy: function whxy(w, h, x, y, n, d) {
      this.width = w;
      this.height = h;
      this.left = x;
      this.top = y;
      this.dataname = n;
      this.ddata = d;
    },
    // 传数据
    chuan: function chuan() {
      console.log(this.data);

      if (this.data.length !== 0) {
        this.$emit('fabufa', this.data);
      } else {
        alert('请添加素材');
      }
    },
    tiao: function tiao(data) {
      this.$router.push({
        path: '/' + data
      });
    }
  }
};
