import echarts from 'echarts';
import { get } from '@/api/axios';
export default {
  name: 'PassenGer',
  data: function data() {
    return {
      zoom: 16,
      center: [116.336496, 39.942178],
      mapStyle: 'amap://styles/whitesmoke',
      // 客流情况趋势图切换1代表客流情况2代表趋势图
      activeName: 'qing',
      linecolor: '#3F6AFF',
      linestyle: 'dashed',
      arrdata: [{
        name: '南巡路线',
        kssj: '07:00',
        jssj: '11:00',
        xcsc: '240分钟',
        xcy: '小明',
        lxfs: '132xxxx0908/ 132xxxx0928'
      }, {
        name: '东巡路线',
        kssj: '07:00',
        jssj: '11:00',
        xcsc: '240分钟',
        xcy: '李明',
        lxfs: '132xxxx0908/ 132xxxx0928'
      }, {
        name: '场馆路线',
        kssj: '07:00',
        jssj: '11:00',
        xcsc: '240分钟',
        xcy: '王红红',
        lxfs: '132xxxx0908/ 132xxxx0928'
      }],
      markers: [],
      option1: {
        title: {
          text: '本月巡查历史统计',
          x: 'left',
          textStyle: {
            fontSize: '1rem',
            color: 'rgba(29,33,51,1)'
          }
        },
        tooltip: {
          trigger: 'item',
          formatter: '本月{b}:{c}次({d}%)'
        },
        legend: {
          orient: 'vertical',
          y: 'center',
          right: '10%',
          data: ['准时率', '早巡率', '晚巡率', '漏巡率']
        },
        series: [{
          name: '客流量',
          type: 'pie',
          radius: ['25%', '40%'],
          center: ['38%', '50%'],
          data: [{
            value: 335,
            name: '准时率',
            itemStyle: {
              color: '#89a2ff'
            }
          }, {
            value: 310,
            name: '早巡率',
            itemStyle: {
              color: '#a2bcff'
            }
          }, {
            value: 234,
            name: '晚巡率',
            itemStyle: {
              color: '#c5d6ff'
            }
          }, {
            value: 135,
            name: '漏巡率',
            itemStyle: {
              color: '#e4e5ff'
            }
          }],
          label: {
            position: 'outside',
            formatter: '{d}%'
          },
          itemStyle: {
            emphasis: {
              shadowBlur: 10,
              shadowOffsetX: 0,
              shadowColor: 'rgba(0, 0, 0, 0.5)'
            }
          }
        }]
      }
    };
  },
  mounted: function mounted() {
    this.drawLine();
    this.geng();
  },
  methods: {
    handleClick: function handleClick(tab, event) {
      console.log(tab, event);
    },
    drawLine: function drawLine() {
      var echarts1 = echarts.init(document.getElementById('echarts1'));
      echarts1.setOption(this.option1);
    },
    linecontent: function linecontent(index) {
      return '<div class="line-marker" style="height: 20px; width: 20px; border-radius: 10px; background-color: rgba(63,106,255, .7); text-align: center; line-height: 20px; margin-top: 20px; color: #fff;">' + (index + 1) + '</div>';
    },
    geng: function geng() {
      var _this = this;

      get('/mngCircuitInfo/getmngcircuitinfobytype', {
        cpid: this.$store.state.user.companyId,
        typeid: 1
      }).then(function (res) {
        console.log(res);
        var data = res.data;
        _this.markers = [];

        for (var i = 0; i < data.length; i++) {
          var path = [];

          for (var y = 0; y < data[i].circuitpointlist.length; y++) {
            path.push([data[i].circuitpointlist[y].circuitPointLon, data[i].circuitpointlist[y].circuitPointLat]);
          }

          _this.markers.push({
            position: [data[i].circuitpointlist[data[i].circuitpointlist.length - 1].circuitPointLon, data[i].circuitpointlist[data[i].circuitpointlist.length - 1].circuitPointLat],
            label: {
              content: data[i].circuitName,
              offset: [25, 0]
            },
            path: path,
            fanglist: data[i].circuitpointlist
          });
        }
      });
      get('/mngpatrol/getmonthPerbycpid', {
        cpid: this.$store.state.user.companyId
      }).then(function (res) {
        _this.option1.series[0].data[0].value = res.data[0]['准点数'] ? res.data[0]['准时率'] : 0;
        _this.option1.series[0].data[1].value = res.data[0]['早到数'] ? res.data[0]['早到数'] : 0;
        _this.option1.series[0].data[2].value = res.data[0]['晚点数'] ? res.data[0]['晚点数'] : 0;
        _this.option1.series[0].data[3].value = res.data[0]['漏巡数'] ? res.data[0]['漏巡数'] : 0;

        _this.drawLine();
      });
      get('/mngpatrol/getMngPatrolPlanlist', {
        cpid: this.$store.state.user.companyId
      }).then(function (res) {
        _this.arrdata = res.data;
      });
    }
  }
};
