result:
.system-container{
  color: #1D2133;

  .bg{
    background-color: #ffffff;
    border-radius: 6px;
    padding: 25px;
    position: relative;
    overflow: hidden;
  }

  p{
    margin: 0;
  }

  .title{
    font-size: 16px;
    font-weight: 550;
    height: 30px;
  }

  .item-title{
    text-indent: 20px;
    position: relative;
    margin-bottom: 15px;
  }

  .item-title::before {
    content: '';
    position: absolute;
    left: 0;
    width: 7px;
    height: 16px;
    background: rgba(63, 106, 255, 1);
    border-radius: 4px;
  }

  .p-btn{
    text-decoration: underline;
    color: rgb(63, 106, 255);
    cursor: pointer;
  }

  .f-r{
    float: right;
  }

  .h-full{
    height: 100%;
  }

  .first-row{
    height: 100%;
  }

  .first-col{
    display: flex;
    flex-direction: column;

    & > div:not(:first-child){
      margin-top: 10px;
    }

    & > div:nth-child(1){
      height: 25%;
    }

    & > div:nth-child(2){
      height: 40%;
    }

    & > div:nth-child(3){
      height: 35%;
    }

    .set-item{
      height: 30px;
      line-height: 30px;
      width: 100%;
      border: 1px solid #C3C6D1;
      border-radius: 4px;
      padding: 0 20px;
      margin-top: 15px;
    }

    .review{
      display: flex;
      flex-direction: column;
      height: calc(100% - 30px);

      .review-item{
        margin: auto 0;
      }
    }

    .integral{
      margin: 20px 0;

      .integral-input{
        width: 30%;
      }
    }
  }

  .tabs{
    height: 30px;
    width: 100%;
    margin-top: 20px;

    .tab-item{
      height: 30px;
      width: 80px;
      line-height: 30px;
      text-align: center;
      border-radius: 4px 4px 0 0;
      display: inline-block;
    }

    .tab-active{
      color: #3f6aff;
      background-color: #E0E8FE;
    }
  }

  .tab-btn{
    float: right;
    height: 30px;
    line-height: 30px;

    i{
      color: #3f6aff;
      font-weight: bolder;
      cursor: pointer;
      font-size: 18px;
    }

    & i:nth-child(1){
      margin-right: 15px;
    }
  }

  .tab-pane{
    height: calc(100% - 80px);
    width: 100%;
    background-color: #F6F8FE;
    padding: 30px;
  }

  .message-title{
    height: 40px;
    line-height: 40px;
    float: left;
    padding: 0 30px;
    background-color: #EDEFF8;
  }

  .message-btn{
    margin-top: 6px;
  }

  .template{
    width: 100%;
    height: 35%;

    .channel-title{
      width: 40%;
      border: 1px solid #E0E7E9;
    }

    .template-title{
      width: 60%;
      border: 1px solid #E0E7E9;
      border-left: none;
    }

    .channel-wapper{
      width: 40%;
      height: calc(100% - 40px);
      border: 1px solid #E0E7E9;
      float: left;
      border-top: none;
      padding-left: 20px;
    }

    .template-wapper{
      width: 60%;
      height: calc(100% - 40px);
      border-right: 1px solid #E0E7E9;
      border-bottom: 1px solid #E0E7E9;
      float: left;
      padding: 10px 25px;
    }
  }

  .plan-wapper{
    height: 30px;
    margin-top: 30px;

    .plan{
      float: left;
      margin-top: 8px;
    }
  }

  .plan-item{
    height: calc(65% - 121px);
  }

  .dialog-footer{
    width: 100%;
    display: flex;
    justify-content: space-around;
    padding: 0 15%;
  }
}
