@import './css/place.css';
.index {
  z-index: 10;
  width: 100%;
  min-width: 690px;
  height: 100%;
  background: #F6F8FF;
  padding: 15px;
  position: relative;
}
ul {
  list-style: none;
  margin: 0;
  padding: 0;
}
.add {
  position: relative;
  width: 100%;
  min-width: 660px;
  height: 60px;
  background:rgba(255,255,255,1);
  box-shadow: 0px 0px 25px 0px rgba(63,106,255,0.08);
  border-radius: 6px;
}
.blue_bar{
  width:6px;
  height:60px;
  background:rgba(63,106,255,1);
  box-shadow:0px 0px 25px 0px rgba(63,106,255,0.08);
  border-radius:6px 0px 0px 6px;
}
.btn_add{
  position: absolute;
  top: 15px;
  left: 36px;
}
.hasData{
  width: 100%;
  height: 60px;
  background: #fff;
  box-shadow:0px 0px 25px 0px rgba(63,106,255,0.08);
  border-radius:6px 0px 0px 6px;
  padding: 22px;
  text-align: center;
  font-size: 16px;
  color: #97a8be;
  margin-top: 20px;
}
.list_box {
  width: 100%;
  min-width: 660px;
  display: flex;
  flex-flow: row nowrap;
  justify-content: flex-start;
}
.list {
  margin-top: 15px;
  width: calc(( 100% - 30px)/3);
  min-width: 210px;
  /* width: 600px; */
  height: 450px;
  background:rgba(255,255,255,1);
  box-shadow: 0px 0px 25px 0px rgba(63,106,255,0.08);
  border-radius: 6px;
  padding: 30px;
}
.list:not(:first-child){
   margin-left: 15px;
}
.list .title{
  width: 100%;
  position: relative;
  height: 18px;
}
.list_name {
  position: absolute;
  left: 0px;
  height: 16px;
  font-size: 16px;
  font-family: PingFang SC;
  font-weight:500;
  color:rgba(29,33,51,1);
  line-height: 18px;
}
.list_stius {
  position: absolute;
  right: 0px;
  height:14px;
  font-size:14px;
  font-family:PingFang SC;
  font-weight:400;
  color:rgba(29,33,51,1);
  line-height:18px;
}
.list_stius span{
  color:#3F6AFF
}
.content{
  margin-top: 30px;
  width: 100%;
}
.content ul{
  width: 100%;
  list-style: none;
  margin: 0;
  padding: 0;
}
.content ul li{
  width: 100%;
  height: 40px;
  display: flex;
  flex-flow: row nowrap;
  justify-content: space-between;
  margin-top: 1px;
}
.content ul li:last-child{
  height: 80px;
}
.content ul li:last-child .name{
  padding: 33px 20px;
}
.content ul li .name {
  width:100px;
  height: 100%;
  padding: 13px 20px;
  background:rgba(237,239,248,1);
  font-size: 14px;
  font-family: PingFang SC;
  font-weight: 500;
  color:rgba(29,33,51,1);
  line-height: 14px;
  text-align: center;
}
.content ul li .things {
  width: calc(100% - 100px);
  height: 100%;
  background:rgba(247,248,255,1);
  padding: 11px;
  font-size: 13px;
  color: #97a8be;
}
.content ul li .things div {
  width: 100%;
  height: 100%;
  overflow: hidden;
  word-break:break-all;
  white-space:normal;
  font-size: 13px;
  color: #97a8be;
  letter-spacing:2px;
  text-overflow:ellipsis;
}
.message_box1 ul {
  padding-top:12px;
}
.message_box1 ul li {
  height: 40px!important;
  line-height: 40px!important;
  padding-left: 13px;
}
/* .box_scroll {
  width: 100%;
  height: 100%;
  padding: 0;
} */
.btn_box {
  width: 100%;
  margin-top: 30px;
  display: flex;
  flex-flow: row nowrap;
  justify-content: space-around;
}
.pages {
  width: 100%;
  display: flex;
  flex-flow: row nowrap;
  justify-content: center;
  margin-top: 30px;
}
/*弹框 增加 编辑 */
.doing_box {
  position: absolute;
  /* display: fixed; */
  left: calc( 50% - 300px);
  top: 5%;
  width: 550px;
  height: 85%;
  background:rgba(255,255,255,1);
  box-shadow:0px 0px 25px 0px rgba(124,124,124,0.3);
  border-radius:6px;
  padding: 30px;
  z-index: 100;
}
.doing_box ul {
  width: 100%;
}
.doing_box ul li {
  width: 100%;
  display: flex;
  flex-flow: row nowrap;
  justify-content: space-between;
}
.doing_box ul li .name {
  width: 57px;
  height: 30px;
  font-size: 14px;
  font-family: PingFang SC;
  font-weight: 400;
  color:rgba(29,33,51,1);
  padding: 8px 0px;
}
.doing_box ul li .selectes {
  /* width: calc(100% - 57px - 20px)!important; */
  height: 30px;
  width: 100%;
  position: relative;
}
.doing_box>ul>li {
  margin-bottom: 20px;
}
.scroll_box {
  /* width: calc(100% - 57px - 20px)!important; */
  width: 100%;
  height: 140px;
}
.scroll_box .text_box {
  width: 100%;
}
.text_box ul {
  background:rgba(243,247,255,1);
  border-radius:4px;
  width: 100%;
  padding: 0 20px 20px 13px;
}
.text_box ul li {
  width: 100%;
  height: 40px!important;
}
.message_box2 ul li {
  padding: 12px 0 12px 0;
}
/* .isuse {
  margin-top: 7px;
} */
.save_cancel {
  width: 100%;
  height: 30px;
  display: flex;
  flex-flow: row nowrap;
  justify-content: center;
}
.scroll_boxa{
  height: 500px;
}
.btn_two {
  text-align: center;
}
