/* eslint-disable */
.integral-wrapper {
  padding: 0;
  margin: 0px;
  height: 100%;
  width: 100%;
  background-color: #F2F5FF;
}
.tab_title {
  height:4rem;
  line-height:4rem;
  width:100%;
  background:rgba(255,255,255,1);
  position:relative;
}
/* tab按钮 */
.tab_title .tab_box {
  list-style:none;
  padding:0px;
  margin:0px;
  align-items: center;
}
.tab_title .tab_box li {
  display:inline-block;
  font-size:16px;
  margin-left:15px;
}
.tab_title .tab_box li:hover {
  cursor: pointer;
}
.tab_title .tab_box li.active{
  color:#3E6AFF;
}
.tab_title .tab_box li.active .show_bar{
  background:#3E6AFF;
}
.tab_title .tab_box .box_name{
  display:flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
}
.tab_title .tab_box .box_name .show_bar{
  width: 20px;
  height: 4px;
  border-radius: 3px;
  margin-top: -13px;
}
/*layout 布局*/
.top_right , .bottom_left .bottom_right{
  position: relative;
}
.top_right .title, .bottom_left .title.bottom_right .title{
  position: absolute;
  font-size: 0.8rem;
  /* text-indent: 10px; */
  font-family: PingFang SC;
  font-weight: 500;
  color: rgba(29, 33, 51, 1);
}
.layout_box{
  width:100%;
  height:100%;
  margin:0px;
  padding:15px;
  display:flex;
  flex-direction: column;
  /* justify-content: space-between; */
  justify-content:flex-start;
}
.layout_box .top ,.layout_box .bottom{
    width:100%;
    display:flex;
    flex-direction: row;
    /* justify-content: space-between; */
     justify-content: flex-start;
}
.layout_box .top {
    height:50%;
    max-height: 380px;
    display:flex;
    flex-direction: row;
    justify-content: flex-start;
    padding:0;
}
.layout_box .bottom {
  margin-top:15px;
  height:50%;
  display:flex;
  flex-direction: row;
  justify-content:flex-start;
  padding:0;
}
.layout_box .top>div ,.layout_box .bottom>div {
  width:50%;
  height:100%;
  background:#fff;
}
/* 第一框css重写 */
.layout_box .top .top_left {
  display:flex;
  flex-direction: row;
  justify-content: flex-start;
}
.layout_box .top .top_left .top_left_left {
  width:100%;
  height: 100%;
}
/* 四个框内边距 */
.layout_box .top .top_left {
  padding:15px 30px 0px 30px;
}
/* 除去第一个框，其他三个框的高度为100% */
.layout_box .top .top_right,
.layout_box .bottom .bottom_left , .layout_box .bottom .bottom_right {
  padding:15px 30px 0px 30px;
  height:100%;
}
.layout_box .top .top_right,.layout_box .bottom .bottom_right {
  margin-left:15px;
}

/* title */
.layout_box .top .title ,.layout_box .bottom .title {
  padding:0;
  margin: 0;
  font-size: 0.8rem;
  /* text-indent: 10px; */
  font-family: PingFang SC;
  font-weight: 500;
  color: rgba(29, 33, 51, 1);
}
.top_left_left .L,.top_left_left .R {
  display:flex;
  justify-content: flex-start;
  height:70px;
  width:100%;
  flex-flow:row nowrap;
  align-content: flex-start;
  margin-top: 30px;
}
.top_left_left .L{
  margin-top: 30px;
}
.top_left_left .L .data_list, .top_left_left .R .data_list {
  /* width:35%; */
  width: 160px;
  height: 100%;
  margin-right: 30px;
  /* max-width:13.7rem; */
  /* height:40%; */
  padding:30px 0px 23px 15px;
  display: flex;
  flex-direction: column;
  /* justify-content: ; */
  justify-content: center;
  border-radius: 8px;
}
.top_left_left .L .data_list div, .top_left_left .R .data_list div {
  font-size: 0.875rem;
  color:#fff;
}
.top_left_left .L .data_list>div .nums , .top_left_left .R .data_list>div .nums {
  font-size: 18px;
  display:inline-block;
  margin-top:5px;
}
.top_left_left .L .data_list {
  background:linear-gradient(94deg,rgba(174,197,255,1) 0%,rgba(139,171,253,1) 100%);
  box-shadow:0px 0px 10px 0px rgba(204,204,204,0.2);
}
.top_left_left .R .data_list {
  background:linear-gradient(90deg,rgba(163,164,255,1) 0%,rgba(140,144,255,1) 100%) !important;
  box-shadow:0px 0px 10px 0px rgba(204,204,204,0.2);
}
.top_left_left .L .data_list>div, .top_left_left .R .data_list>div {
  text-align:left;
  color:#fff;
}
