/* eslint-disable */

$width_left:50%;// top_left 宽度
$width_right:50%;// top_right 宽度
$font_num:30px;//top_left 下面变大字体的大小
$font-change_before:24px;
// --width-primary

.integral-wrapper{
  padding: 0;
  margin:0px;
  height: 100%;
  width:100%;
  background-color: #F2F5FF;
}
.tab_title{
    height:55px;
    width:100%;
    background:rgba(255,255,255,1);
    position:relative;
    position:fixed;
    
}
/* tab按钮 */
.tab_title .tab_box {
    position:absolute;
    bottom:0px;
     list-style:none;
     padding:0px;
     margin:0px;
     align-items: center;
     height:36px;
}
.tab_title .tab_box li{  
  display:inline-block;
  font-size:16px;
  height:100%;
  margin-left:15px;
  color:#1D2133;
}
.tab_title .tab_box li:hover{
    cursor: pointer;
}
.tab_title .tab_box li.active{
    color:#3E6AFF;
}
.tab_title .tab_box li.active .show_bar{
    background:#3E6AFF;
}
.tab_title .tab_box .box_name{
    display:flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
}
.tab_title .tab_box .box_name .show_bar{
    width: 20px;
    height: 4px;
    border-radius: 3px;
    margin-top: 5px;
}
/*layout 布局*/
.top_right , .bottom_left .bottom_right{
    position: relative;
}
.top_right .title, .bottom_left .title.bottom_right .title{
    position: absolute;
    font-size: 0.8rem;
    /* text-indent: 10px; */
    font-family: PingFang SC;
    font-weight: 500;
    color: rgba(29, 33, 51, 1);
}
.layout_box{
    width:100%;
    height:100%;
    margin:0px;
    padding:70px 15px 15px 15px;
    display:flex;
    flex-direction: column;
    /* justify-content: space-between; */
    justify-content:flex-start;
  
}
.layout_box .top ,.layout_box .bottom{
    width:100%;
    display:flex;
    flex-direction: row;
    /* justify-content: space-between; */
     justify-content: flex-start;
}
.layout_box .top {
    height:50%;
    display:flex;
    flex-direction: row;
    justify-content: flex-start;
    padding:0;
}
.layout_box .bottom{
    margin-top:15px;
    height:50%;
    display:flex;
    flex-direction: row;
    justify-content:flex-start;
    padding:0;
}
.layout_box .top>div ,.layout_box .bottom>div{
    width:50%;
    height:100%;
    background:#fff;
}
/* 第一框css重写 */
.layout_box .top .top_left {
    display:flex;
    flex-direction: row;
    justify-content: flex-start;
    width:$width_left;
}
.layout_box .top .top_right {
    width:$width_right;
}
.layout_box .top .top_left .top_left_left{
    width:100%;
}
/* top_left下 四个框内边距 */
.layout_box .top .top_left{
    padding:15px 30px 10px 30px;
}
/* 除去第一个框，其他三个框的高度为100% */
.layout_box .top .top_right,
.layout_box .bottom .bottom_left , .layout_box .bottom .bottom_right{
    padding:15px 30px 0px 30px;
    height:100%;
}
/* 第2 4个大框marin调整 */
.layout_box .top .top_right,
.layout_box .bottom .bottom_right{
    margin-left:15px;
}

.top_left_left .echarts{
  height:100%;
  width:100%;
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
}
.left_ulbox {
  width: 100%;
  height: calc(100% - 29px)!important;
  margin-top: 30px;
}
.left_ulbox ul{
  list-style: none;
  margin: 0;
  padding: 0;
  width: 100%;
  height: 70px;
  display: flex;
  flex-flow: row nowrap;
  justify-content: flex-start;
}
.left_ulbox ul .names{
  width: 100%;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}
.left_ulbox ul li {
  // width: calc((100% - 45px)/3);
  // height: 100%;
  // max-width: 180px;
  // max-height: 90px;
  width: 160px;
  height:70px;
  margin-right: 30px;
  font-size:14px;
  font-family:PingFang SC;
  font-weight:500;
  border-radius: 5px;
  color:rgba(255,255,255,1);
  background:linear-gradient(94deg,rgba(174,197,255,1) 0%,rgba(139,171,253,1) 100%);
  box-shadow:0px 0px 10px 0px rgba(204,204,204,0.2);
  padding: 10px 15px 10px 10px;
  display: flex;
  flex-direction: column;
  justify-content: space-around;
}
.left_ulbox ul li .nums_box{
  width: 100%;
  font-size:14px;
  font-family:DIN Condensed;
  font-weight:bold;
  color:rgba(255,255,255,1);
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}
.left_ulbox ul li .nums_box span{
  display: inline-block;
  font-size:14px;
  font-family:PingFang SC;
  font-weight:500;
  color:rgba(255,255,255,1);
}
.bottom_ulbox {
  margin-top: 30px !important;
}
.bottom_ulbox li{
  background:linear-gradient(90deg,rgba(163,164,255,1) 0%,rgba(140,144,255,1) 100%) !important;
  box-shadow:0px 0px 10px 0px rgba(204,204,204,0.2);
}
/* title */
.layout_box .top .title ,.layout_box .bottom .title{
    padding:0;
    margin: 0;
    font-size: 0.8rem;
    /* text-indent: 10px; */
    font-family: PingFang SC;
    font-weight: 500;
    color: rgba(29, 33, 51, 1);
}
