@import './css/messageset.css';
/*简单初始化---star*/
ul {
  list-style: none;
  margin: 0;
  padding: 0;
}
/*简单初始化---end*/
.index {
  z-index: 10;
  width: 100%;
  min-width: 690px;
  height: 100%;
  background: #F6F8FF;
  padding: 15px;
  position: relative;
  display: flex;
  flex-flow: row nowrap;
  justify-content: space-between;
}
/* 左侧 */
.message_tree{
  width: 360px;
  height: 100%;
  padding: 30px;
  background:rgba(255,255,255,1);
  box-shadow:0px 0px 25px 0px rgba(63,106,255,0.08);
  border-radius:6px;
  position: relative;
}
.message_tree .title{
  width: 100%;
  /* height: 30px;
  line-height: 30px;
  padding: 7px 0 7px 0; */
  display: flex;
  flex-flow: row nowrap;
  justify-content: space-between;
}
.message_tree .title .names{
  width:99px;
  height:30px;
  padding: 7px 0 7px 0;
  font-size:16px;
  font-family:PingFang SC;
  font-weight:500;
  color:rgba(29,33,51,1);
  line-height:18px;
}
.message_tree .title .operation{
  width: 90px;
  height: 100%;
}
.message_tree .title .operation .ad_btn,.message_tree .title .operation .del_btn{
  display: inline-block;
  width:30px;
  height:16px;
  font-size:14px;
  font-family:PingFang SC;
  font-weight:500;
  text-decoration:underline;
  color:rgba(63,106,255,1);
  line-height:18px;
  cursor: pointer;
}
.list_title {
  margin-top: 60px;
  width: 100%;
  height: calc(100% - 20px - 60px);
}
.list_title p{
  width: 100%;
  height: 20px;
  line-height: 20px;
  text-align: center;
  color: #D4DADF;
  font-size: 16px;
}
.list_title .scroll_box{
  width: 100%;
  height: 100%;
}
.list_title ul li{
  width: 100%;
  height: 40px;
  display: flex;
  flex-flow: row nowrap;
  justify-content: space-between;
  padding: 10px 0 10px 0;
  cursor: pointer;
}
.active .namea{
  color:rgba(63,106,255,1)!important;
}
.list_title ul li .namea {
  height:14px;
  font-size:14px;
  font-family:PingFang SC;
  font-weight:400;
  color:rgba(29,33,51,1);
  line-height:18px;
}
/* 右侧 */
.content{
  width: calc(100% - 15px - 360px);
  min-width: 650px;
  height: 100%;
  padding: 50px 0 0 50px;
  background:rgba(255,255,255,1);
  box-shadow:0px 0px 25px 0px rgba(63,106,255,0.08);
  border-radius:6px;
}
.content ul{
  width: 100%;
  height: 100%;
  min-width: 650px;
}
.content ul li{
  height: 40px;
  line-height: 40px;
  width: 550px;
  display: flex;
  flex-flow: row nowrap;
  justify-content: space-between;
  margin-bottom: 20px;
}
.content ul li:nth-last-child(2){
  padding-left: 95px;
}
.content ul li:last-child{
  padding-left: 95px;
  display: flex;
  flex-flow: row nowrap;
  justify-content: center;
}
.content ul li .title_name{
  width:150px;
  height:13px;
  font-size:14px;
  text-align: right;
  font-family:PingFang SC;
  font-weight:400;
  color:rgba(29,33,51,1);
}
.content ul li .rights{
  min-width: 380px;
  width: calc(100% - 20px - 150px);
}
.moreContent{
  height: 140px!important;
}
.moreContent .rights{
  height: 140px!important;
  line-height: 140px;
}
.isuse{
  margin-top: 10px;
}
