import { get } from '../../api/axios.js'
export default {
  data() {
    return {
      data: {},
      arrdata: [],
      arr: [],
      pagesize: 3,
      pageNumber: 2,
      all: 0, //      总页数
      cur: 1,
      pickerOptions1: {
        disabledDate(time) {
          return time.getTime() > Date.now()
        },
        shortcuts: [{
          text: '今天',
          onClick(picker) {
            picker.$emit('pick', new Date())
          }
        }, {
          text: '昨天',
          onClick(picker) {
            const date = new Date()
            date.setTime(date.getTime() - 3600 * 1000 * 24)
            picker.$emit('pick', date)
          }
        }, {
          text: '一周前',
          onClick(picker) {
            const date = new Date()
            date.setTime(date.getTime() - 3600 * 1000 * 24 * 7)
            picker.$emit('pick', date)
          }
        }]
      },
      value1: '',
      value2: '',
      value: '',
      shij: [new Date(2019, 9, 1), new Date()],
      options: [
        {
          value: '0',
          label: '确认'
        },
        {
          value: '1',
          label: '取消确认'
        }
      ],
      options2: [],
      lx: [],
      cities: [
        {
          value: 'haikang',
          label: '设备告警'
        },
        {
          value: 'dahua',
          label: '环境告警'
        },
        {
          value: 'huawei',
          label: '主动告警'
        },
        {
          value: 'bxgj',
          label: '报修告警'
        },
        {
          value: 'tjgj',
          label: '统计告警'
        },
        {
          value: 'clgj',
          label: '车辆告警'
        }
      ],
      value6: '',
      value7: '',
      options5: [
        {
          value: 'pc',
          label: 'PC端'
        },
        {
          value: 'mobile',
          label: '移动端'
        }
      ]
    }
  },
  mounted() {
    var that = this
    get('/assetlogs/selectAlarmLogs', {
      pageNumber: that.cur - 1,
      pageSize: 8
    }).then(res => {
      that.arrdata = res.content
      that.all = res.numberOfElements
      console.log(res, 12212)
    })
    get('/alarmInfo/selectCountAlarmType', {}).then(res => {
      this.options2 = res
      console.log(111)
    })
  },
  methods: {
    sou() {
      var that = this
      get('/assetlogs/selectAlarmLogs', {
        pageNumber: that.cur - 1,
        pageSize: 8,
        operate_type: this.data.value5,
        alarm_type: this.data.alarm_type,
        operate_man: this.data.name,
        startTime: this.shi2(that.shij[0]),
        endTime: this.shi2(that.shij[1])
      }).then(res => {
        that.arrdata = res.content
        this.all = res.numberOfElements
        console.log(res, 100)
      })
    },
    clickok() {
      this.zh = false
      this.zhdata = []
    },
    zhfa(i) {
      console.log(i)
      this.zh = true
      this.zhdata = this.arr[i]
    },
    handleSelectionChange(val) {
      this.multipleSelection = val
    },
    btnClick(data) {
      console.log(data)
      this.cur = data
      this.shua()
    },
    shua() {
      // console.log(this.cur, this.arrdata)
      // this.arrdata = []
      // for (var i = 0; i < 10; i++) {
      //   if (i < this.arr.length && this.arr[i + this.cur * 8 - 8]) {
      //     this.arrdata.push(this.arr[i + this.cur * 8 - 8])
      //   }
      // }
    },
    shi(value) {
      var s = value / 1000
      var m = parseInt(s % 60)
      s = parseInt(s / 60)
      var f = parseInt(s % 60)
      s = parseInt(s / 60)
      var date = (s < 10 ? '0' + s : s) + ':' + (f < 10 ? '0' + f : f) + ':' + (m < 10 ? '0' + m : m)
      return date
    },
    shi2(date) {
      var n = date.getFullYear()
      var y = date.getMonth() + 1
      var r = date.getDate()
      var s = date.getHours() < 10 ? '0' + date.getHours() : date.getHours()
      var f = date.getMinutes() < 10 ? '0' + date.getMinutes() : date.getMinutes()
      var m = date.getSeconds() < 10 ? '0' + date.getSeconds() : date.getSeconds()
      return n + '-' + y + '-' + r + ' ' + s + ':' + f + ':' + m
    }
  }
}
