import { get } from '../../api/axios.js'
import echarts from 'echarts'
export default {
  data() {
    return {}
  },
  watch: {},
  mounted() {
    get('/alarmInfo/selectCountAlarmType', {}).then(response => {
      console.log(111)
      this.snowTurnoveraa(response)
    })
    get('/alarmInfo/selectCountAlarmSource', {}).then(response => {
      console.log(122)
      this.snowTurnover(response)
    })
  },
  methods: {
    snowTurnover(dta) {
      var snowTurnover = echarts.init(
        document.getElementById('snowTurnover')
      )
      snowTurnover.resize()
      window.addEventListener('resize', () => {
        snowTurnover.resize()
      })
      var aaa = {
        color: [
          '#8aa3ff',
          '#96bbf2',
          '#c2d8ff',
          '#e4e6ff',
          '#ff999a',
          '#ffdd65',
          '#d4c0ff'
        ],
        tooltip: {
          trigger: 'item',
          formatter: '告警源占比<br/>{b}:{c}次({d}%)'
        },
        legend: {
          top: 20,
          left: 'center',
          data: (function() {
            var data = []
            for (var i = 0; i < dta.length; i++) {
              data.push(dta[i].source_name)
            }
            return data
          })()
        },
        toolbox: {
          show: true,
          feature: {
            mark: { show: true },
            magicType: {
              show: true,
              type: ['pie', 'funnel'],
              option: {
                funnel: {
                  x: '25%',
                  width: '50%',
                  funnelAlign: 'center',
                  max: 1548
                }
              }
            }
          }
        },
        series: [
          {
            left: ['80%', '80'],
            name: '',
            type: 'pie',
            radius: ['30%', '50%'],
            itemStyle: {
              normal: {
              }
            },
            label: {
              position: 'outside',
              formatter: '{d}%'
            },
            data: (function() {
              var data = []
              for (var i = 0; i < dta.length; i++) {
                data.push({
                  value: dta[i]['数量'],
                  name: dta[i].source_name
                })
              }
              console.log(data)
              return data
            })()
          }
        ]
      }
      console.log(aaa)
      snowTurnover.setOption(aaa)
    },
    snowTurnoveraa(dta) {
      var snowTurnoveraa = echarts.init(
        document.getElementById('snowTurnoveraa')
      )
      snowTurnoveraa.resize()
      window.addEventListener('resize', () => {
        snowTurnoveraa.resize()
      })
      var aaa = {
        color: [
          '#8aa3ff',
          '#96bbf2',
          '#c2d8ff',
          '#e4e6ff',
          '#ff999a',
          '#ffdd65',
          '#d4c0ff'
        ],
        tooltip: {
          trigger: 'item',
          formatter: '告警类型占比<br/>{b}:{c}次({d}%)'
        },
        legend: {
          top: 20,
          left: 'center',
          data: (function() {
            var data = []
            for (var i = 0; i < dta.length; i++) {
              data.push(dta[i].alarm_type_name)
            }
            return data
          })()
        },
        toolbox: {
          show: true,
          feature: {
            mark: { show: true },
            magicType: {
              show: true,
              type: ['pie', 'funnel'],
              option: {
                funnel: {
                  x: '25%',
                  width: '50%',
                  funnelAlign: 'center',
                  max: 1548
                }
              }
            }
          }
        },
        series: [
          {
            left: ['80%', '80'],
            name: '',
            type: 'pie',
            radius: ['30%', '50%'],
            itemStyle: {
              normal: {
              }
            },
            label: {
              position: 'outside',
              formatter: '{d}%'
            },
            data: (function() {
              var data = []
              for (var i = 0; i < dta.length; i++) {
                data.push({
                  value: dta[i]['数量'],
                  name: dta[i].alarm_type_name
                })
              }
              console.log(data)
              return data
            })()
          }
        ]
      }
      console.log(aaa)
      snowTurnoveraa.setOption(aaa)
    }
  }
}
