result:
.retrieve-container{
  width: 100%;
  height: 100%;
  background-color: #f0f2ff;

  .logo-wapper{
    width: 100%;
    height: 50px;
    background-color: #121430;
  }

  .retrieve-form{
    width: 98%;
    margin-left: 1%;
    margin-top: 12px;
    height: 500px;
    text-align: center;
    padding: 35px;
    background-color: #fff;

    .steps {
      margin-top: 50px;
      margin-left: 25%;
      width: 50%;

      .el-step__title{
        font-size: 12px !important;
      }

      .is-wait{
        .is-text{
          background-color: #C8CCD6;
          border-color: #C8CCD6;
        }
      }

      .is-process{

        .el-step__line{
          left: 0;
          background-color: #3F6AFF;

          .el-step__line-inner{
            display: none;
          }
        }

        .is-text{
          background-color: #3F6AFF;
          border-color: #3F6AFF;
        }

        .is-text:after{
          content: '';
          position: absolute;
          width: 30px;
          height: 30px;
          border: 4px solid rgba(63,106,255,.2);
          border-radius: 50%;
        }
      }

      .is-success{
        .el-step__line{
          background-color: #3F6AFF;

          .el-step__line-inner{
            display: none;
          }
        }

        .is-text{
          background-color: #3F6AFF;
          border-color: #3F6AFF;
        }
      }

      .el-step__main .is-success,
      .el-step__main .is-process{
        color: #3F6AFF;
      }

      .el-step__line{
        left: 0;
        right: 0;
      }

      .el-step__icon-inner{
        color: #fff;
      }

      .el-step:last-of-type .el-step__line{
        display: block;
      }
    }

    .el-input {
      display: inline-block;
      height: 37px;
      width: 85%;
      font-size: 12px !important;

      input {
        background: transparent;
        border: 0px;
        -webkit-appearance: none;
        border-radius: 0px;
        padding: 0 5px 0 15px;
        color: #000;
        height: 35px;
        caret-color: #000;

        &:-webkit-autofill {
          box-shadow: 0 0 0px 1000px #F6F6F8 inset !important;
          -webkit-text-fill-color: #000 !important;
        }
      }
    }

    .el-form-item {
      background: rgba(246, 246, 248, 1);
      border-radius: 5px;
      border: 1px solid rgb(246, 246, 248);
      box-sizing: border-box;
    }

    .input-focus{
      border: 1px solid #638AF1;
    }

    .username{
      margin: 50px auto 0;
      width: 260px;

      .code{
        width: 140px;
        padding-left: 8px;
        float: left;
      }

      .s-identify{
        float: right;
      }

      .mobile-code{
        cursor: pointer;
        float: right;
        width: 100px;
        height: 30px;
        line-height: 30px;
        margin-top: 3px;
        color: #638AF1;
        font-size: 12px;
        border: 1px solid #638AF1;
        border-radius: 5px;
      }

      .jump{
        font-size: 14px;

        & .jump-svg{
          display: block;

          & .svg-icon{
            height: 30px;
            width: 30px;
            margin-bottom: 22px;
          }
        }

        .jump-a{
          font-size: 12px;
          color: #3F6AFF;
          text-decoration: underline;
        }
      }
    }

    .input-yzm{

      .el-input{
        width:105px;
      }

      .el-form-item__error{
        left: -8px;
      }
    }
  }
}
