import { get } from '../../api/axios.js'
export default {
  name: 'About',
  data() {
    return {
      // 分页
      adminlist: [],
      options: [
        {
          value: '选项1',
          label: '检修'
        }
      ],
      value: '',
      cities: [
        {
          value: 'aa',
          label: '未修好'
        },
        {
          value: 'ab',
          label: '未修理'
        },
        {
          value: 'ac',
          label: '修理中'
        },
        {
          value: 'ac2',
          label: '修理好'
        }
      ],
      value6: '',
      pagesize: 3,
      pageNumber: 2,
      all: 0, //      总页数
      cur: 1,
      arrdata: [],
      arr: [], //    分页
      mingc: '',
      ip: '',
      type: '',
      data: {},
      zhdata: {},
      zh: false,
      xgdata: {},
      xg: false,
      multipleSelection: [],
      zhfadata: {}
    }
  },
  computed: {
  },
  watch: {
  },
  mounted() {
    this.shua()
    var that = this
    get('/user/list/cp12').then(res => {
      console.log(1234)
      that.adminlist = res.data
    })
    get('/overhaul/getOverhaul', {
      pageNumber: that.cur - 1,
      pageSize: 8
    }).then(res => {
      that.arrdata = res.content
      that.all = res.totalElements
      console.log(res, 111)
    })
  },
  methods: {
    clickok() {
      this.zh = false
      this.zhdata = []
      this.xg = false
    },
    zhfa(i) {
      console.log(i)
      this.zh = true
    },
    xgfa(i) {
      console.log(i)
      this.xg = true
      this.xgdata = this.arr[i]
    },
    handleSelectionChange(val) {
      this.multipleSelection = val
    },
    btnClick(data) {
      console.log(data)
      this.cur = data
      this.shua()
    },
    shua() {
      console.log(this.cur, this.arrdata)
      this.arrdata = []
      for (var i = 0; i < 5; i++) {
        if (i < this.arr.length && this.arr[i + (this.cur * 8) - 8]) {
          this.arrdata.push(this.arr[i + (this.cur * 8) - 8])
        }
      }
    },
    ck() {
      var that = this
      get('/overhaul/getOverhaulById?id=1', {
      }).then(res => {
        that.arrdata = res.data.content
        console.log(res, 122)
      })
      this.clickok()
    }
  }
}
