export default {
  name: 'Tourist',
  // components: {
  //   TableGet
  // },
  data() {
    return {
      border: false, // 是否有纵向线
      sortable: false, // 是否排序
      highlightcurrentrow: true, // 是否高亮
      currentPage: 1, // 默认前往的页码
      total: 100, // 总页数
      pageNumber: 1, // 当前页的页码数默认1
      pageshow: false,
      questions: false, // 对话框数据对接
      answers: false,
      formLabelWidth: '120px',
      form: {
        name: '',
        keywords: '',
        container: ''
      },
      index: '',
      row: {},
      alldata: {
        currentPage: 1,
        total: 100,
        sortable: false,
        tableData: [{
          index: 1,
          question: '问题122',
          keywords: '问题1',
          containe: '回答问题1'
        }, {
          index: 2,
          question: '问题22',
          keywords: '问题2',
          containe: '回答问题2'
        }, {
          index: 4,
          question: '问题3',
          keywords: '问题3',
          containe: '回答问题3'
        }, {
          index: 4,
          question: '问题4',
          keywords: '问题4',
          containe: '回答问题4'
        }]
      }
    }
  },
  computed: {
  },
  mounted() {
  },
  methods: {
    onchange(val) {
      console.log('获取到的值' + val)
    },
    // 修改问题
    handleEdit(index, row) {
      // console.log(index, row)
      // 初始化
      this.form.name = row.question
      this.form.keywords = row.keywords
      this.questions = true
      this.index = index
      this.row = row
    },
    // 修改问题保存
    handsave() {
      this.alldata.tableData[this.index].question = this.form.name
      this.alldata.tableData[this.index].keywords = this.form.keywords
      this.questions = false
    },
    // 修改回答内容
    handleContainer(index, row) {
      // console.log(index, row)
      // 初始化
      this.form.container = row.containe
      this.answers = true
      this.index = index
      this.row = row
    },
    // 修改回答内容保存
    handContainersave() {
      this.alldata.tableData[this.index].containe = this.form.container
      this.answers = false
    },
    handleSizeChange(val) {
      console.log(`每页 ${val} 条`)
    },
    handleCurrentChange(val) {
      this.pageNumber = val
      console.log('当前页：' + val)
    },
    formatter(row, column) {
      return row.address
    }
  }
}
