.about {
  width: 100vw;
  height: 100vh;
  background: #122f7e;
}
/* 地图 */
/* 监视 */
.cctv {
  float: right;
  width: 47%;
  height: 85%;
  margin-right: 1.5%;
  border: 3px solid #6edcfa;
  box-shadow: 0 0 10px #ffffff;
}
/* 头部标签 */
.header {
  height: 8%;
  width: 100%;
  margin-bottom: 10px;
  background-image: url("../../assets/yingzhi/topback.png");
  background-repeat: no-repeat;
  background-size: 100%;
}
.header_text {
  width: 30%;
  text-align: center;
  color: #fff;
  line-height: 50px;
  font-size: 20px;
  letter-spacing: 10px;
  float: left;
  position: absolute;
  left: 50%;
  top: 0%;
  margin-left: -15%;
}
.daoy {
  position: relative;
  left: 20px;
  top: 5px;
  z-index: 1000000;
}
button {
  border-radius: 5px;
  background: #1f3a89;
  height: 25px;
  padding: 0 10px 0 10px;
  outline: none;
  border: 1px solid #fff;
  color: #6fdafc;
}
.backa {
  width: 1360px;
  height: 510px;
  border: 1px solid #0d6ca6;
  margin-left: 17px;
  margin-top: 10px;
}
#container {
  width: 98%;
  height: 85%;
  margin: 13px;
  position: absolute;
  bottom: 15px;
}
.dian {
  width: 100px;
  height: 80%;
  position: absolute;
  left: 1.6%;
  top: 17%;
  z-index: 9999;
  cursor: pointer;
}
.hengd {
  width: 90px;
  height: 25px;
  border-radius: 0 100px 100px 0;
  margin-bottom: 10px;
  margin-top: 10px;
  color: #fff;
  font-size: 13px;
  line-height: 25px;
  text-align: right;
  padding-right: 10px;
  z-index: 9999;
  background: #1e3b88;
}
.hengd div {
  padding-right: 20px;
}
.hengd2 div {
  background: rgba(00, 00, 00, 0.5);
  width: 222%;
  height: 25px;
  margin-top: -25px;
  z-index: 9999;
  border-radius: 0 100px 100px 0;
  display: block;
  animation: dong1 0.5s;
  overflow: hidden;
}
@keyframes dong1 {
  0% {
    width: 0%;
    color: #000;
  }
  100% {
    width: 180%;
    color: #fff;
  }
}
.hengd1 div {
  display: none;
}
button:hover {
  background: #151748;
  color: #fefffb;
}
