    date(date) {
      const d = new Date(date)
      const resDate = d.getFullYear() + '-' + this.p((d.getMonth() + 1)) + '-' + this.p(d.getDate())
      // const resTime = this.p(d.getHours()) + ':' + this.p(d.getMinutes()) + ':' + this.p(d.getSeconds())
      return resDate
    },
    p(s) {
      return s < 10 ? '0' + s : s
    }
  }
