import { get } from '@/api/axios'
export default {
  name: 'Tourist',
  data() {
    return {
      cpid: null,
      default_bar: 0,
      name: '',
      messages_tart: '',
      messages_content: '',
      messages_end: '',
      isused: false,
      cities: ['短信', '手机游景区', '手机管景区'],
      disabled: true,
      edit: false,
      list_value: [
        {
          name: '短信',
          value: false
        },
        {
          name: '手机游景区',
          value: false
        },
        {
          name: '手机管景区',
          value: false
        }
      ],
      options: [{
        value: '选项1',
        label: '黄金糕'
      }, {
        value: '选项2',
        label: '双皮奶'
      }, {
        value: '选项3',
        label: '蚵仔煎'
      }, {
        value: '选项4',
        label: '龙须面'
      }, {
        value: '选项5',
        label: '北京烤鸭'
      }]
    }
  },
  computed: {
    datas: function() {
      if (this.list_value.length > 0) {
        return false
      } else {
        return true
      }
    },
    save: function() {
      return (this.name && this.messages_tart && this.messages_content && this.messages_end)
    }
  },
  watch: {
  },
  created() {
    this.cpid = this.$store.state.user.companyId
  },
  mounted() {
    // 渠道列表获取
    this.initChannel()
    // 渠道列表点击
    this.Clickli(0)
    // 模板列表获取
    this.moduleName()
  },
  methods: {
    addClick() {
      var that = this
      this.$prompt('请输入渠道名称', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        inputPattern: /\S{3,}/,
        inputErrorMessage: '请检查渠道名称是否正确(不少于三个任意字符)'
      }).then(({ value }) => {
        // this.$message({
        //   type: 'success',
        //   message: '你的邮箱是: ' + value
        // })
        if (value) {
          that.list_value.push(
            {
              name: value,
              value: false
            }
          )
          that.Clickli(that.list_value.length - 1)
        }
      }).catch(() => {
        this.$message({
          type: 'info',
          message: '取消输入'
        })
      })
    },
    delClick() {
      var that = this
      var val = that.list_value[that.default_bar].name
      this.$confirm('<span>是否删除:&nbsp;&nbsp;<span style="color: red;">' + val + '</span></span>', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'error',
        dangerouslyUseHTMLString: true,
        center: true
      }).then(() => {
        if (that.default_bar || that.default_bar === 0) {
          that.list_value.splice(that.default_bar, 1)
          that.default_bar = null
        }
      }).catch(() => {
        this.$message({
          type: 'message',
          message: '已取消删除'
        })
      })
    },
    onchange(val) {
      var that = this
      var list = this.options
      console.log(list)
      for (var i = 0; i < list.length; i++) {
        if (list[i].value === val) {
          that.messages_content = list[i].content
        }
      }
    },
    changeClick() {
      this.disabled = false
      // this.edit = true
      console.log('当前修改的是' + this.default_bar)
    },
    // 渠道列表获取
    initChannel() {
      var that = this
      get('/notify-template/' + that.cpid, null).then(response => { // 获取渠道通知模板
        if (response.status === 0) {
          var list = response.data
          var list_value = []
          for (var i = 0; i < list.length; i++) {
            list_value.push({
              name: list[i].wayName,
              value: false
            })
          }
          that.list_value = list_value
        }
      })
    },
    // 模板名称获取
    moduleName() {
      var that = this
      get('/notify-template/' + that.cpid).then(data => {
        console.log(data)
        if (data.msg === 'SUCCESS') {
          var options = []
          var list = data.data
          for (var i = 0; i < list.length; i++) {
            options.push({ 'value': list[i].id, 'label': list[i].wayName, 'content': list[i].content })
          }
          that.options = options
        }
      })
    },
    saveClick() {
      this.edit = false
      this.disabled = true
      // 保存获取到渠道的选项
      console.log(this.options)
      // that.options = options

      // 渠道的配置项
      // that.list_value
      // this.name
      // this.messages_tart
      // this.messages_content
      // this.messages_end
      // this.isused
      console.log(this.list_value)
    },
    Clickli(index) {
      // window.event.stopPropagation()
      this.default_bar = index
      var that = this
      for (var i = 0; i < that.list_value.length; i++) {
        if (i === index) {
          that.list_value[i].value = !that.list_value[i].value
        }
      }
      // 重置渠道配置
      this.name = null
      this.messages_tart = null
      this.messages_content = null
      this.messages_end = null
      this.isused = false

      this.disabled = true
    },
    switchClick(index) {
      var that = this
      that.default_bar = index
      window.event.stopPropagation()
      // for (var i = 0; i < that.list_value.length; i++) {
      //   if (i !== index) {
      //     that.list_value[i].value = false
      //   }
      // }
    }
  }
}
