$bg:#121430;
$dark_gray:#889aa4;
$light_gray:#eee;

.login-container {
  min-height: 100%;
  width: 100%;
  min-width: 1024px;
  background-color: $bg;
  overflow: hidden;

  .login-text {
    position: absolute;
    top: 35%;
    left: 15%;
    width: 383px;
    height: 286px;
  }

  .login-form {
    position: absolute;
    width: 320px;
    height: 265px;
    right: 10%;
    top: 0;
    bottom: 0;
    margin: auto 0;
    padding: 0 25px;
    background-color: #fff;
    border-radius: 5px;
    color: #000;
    overflow: hidden;
  }

  .tips {
    font-size: 12px;
    color: #fff;
    margin-bottom: 10px;

    span {
      &:first-of-type {
        margin-right: 16px;
      }
    }
  }

  .svg-container {
    padding: 0 5px 0 15px;
    color: $dark_gray;
    vertical-align: middle;
    width: 30px;
    display: inline-block;
  }

  .show-pwd {
    position: absolute;
    right: 18px;
    top: 0;
    font-size: 12px;
    color: $dark_gray;
    cursor: pointer;
    user-select: none;
  }

  .thirdparty-button {
    position: absolute;
    right: 0;
    bottom: 6px;
  }

  @media only screen and (max-width: 470px) {
    .thirdparty-button {
      display: none;
    }
  }
}
