.about {
  width: 100%;
  height: 100%;
  position: fixed;
  top: 0;
  left: 0;
  background-image: url("../../../assets/yingzhi/bg_img.png");
  background-repeat: no-repeat;
  background-size: 100%;
}
/* 头部标签 */
.header {
  height: 8%;
  width: 100%;
  margin-bottom: 10px;
  background-image: url("../../../assets/yingzhi/topback.png");
  background-repeat: no-repeat;
  background-size: 100%;
}
.daoy {
  position: relative;
  left: 20px;
  top: 5px;
  z-index: 999;
}
.header_text {
  width: 30%;
  text-align: center;
  color: #fff;
  line-height: 50px;
  font-size: 20px;
  letter-spacing: 10px;
  float: left;
  position: absolute;
  left: 50%;
  top: 0%;
  margin-left: -15%;
}
.echarts_box{
  width: 100%;
  height: calc(100% - 8%);
  padding: 0 23px 30px 23px;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
}
.echarts_box .top ,.echarts_box .bottom{
  position: relative;
  width: 100%;
  height: calc((100% - 34.5px)/2);
}
.echarts_box .top {
  padding: 10px 10.5px 14.5px 10.5px;
}
.echarts_box .bottom{
  padding: 0;
}
.echarts_box .top .table , .echarts_box .bottom .table{
  width: 100%;
  height: calc(100% - 28.5px - 15px);
  margin-top: 15px;
}
.title{
  width: 100%;
  height: 16px;
  line-height: 16px;
  text-align: center;
  font-size:16px;
  font-family:Adobe Heiti Std;
  font-weight: 400;
  color:rgba(255,252,1,1);
  margin-top: 12.5px;
}
.tab_table{
  width: 100%;
  height: 100%;
}
.tab_table tr{
  width: 100%;
  height: 20px;
  font-size:12px;
  font-family:Adobe Heiti Std;
  color:rgba(255,255,255,1);
}
.tab_table tr:nth-child(1){
  font-weight: 400;
}
.tab_table tr td{
  text-align:center;
}
.tab_table tr th .money{
  display: inline-block;
  font-size: 10px;
}
.ul_boxtitle{
  width: calc(100% - 4px);
  height: 100%;
  list-style: none;
  margin: 0;
  padding: 2px;
  display: flex;
  flex-flow: row wrap;
}
.ul_boxtitle li {
  display:inline-block;
  width: calc((100% - 21px)/7);
  height:35px;
  margin-right: 3px;
  margin-bottom: 3px;
  line-height:35px;
  position:relative;
}
.ul_boxtitle li span.content_title{
  display: inline-block;
  position: absolute;
  width: 100%;
  height: 100%;
  text-align: center;
  line-height: 35px;
  font-size: 12px;
  font-family:Adobe Heiti Std;
  font-weight:normal;
  color:rgba(255,255,255,1);
}
.bg_opa{
  position:absolute;
  width: 100%;
  height: 100%;
  top:0;
  left: 0;
  background:rgba(31,58,137,1);
  opacity: 0.8;
  z-index: -1;
}
.ul_boxtitle li img{
  display:inline-block;
  height:14px;
  width:14px;
  position:absolute;
  left:calc(80% - 7px);
  top:calc(50% - 7px);
}
