.income-chart-wrapper{
  background-color: #ffffff;
  height: 100%;

  & .el-tabs__item + .is-top,
  & .el-tabs__item:first-child{
    background-color: #E4EBFF;
  }

  & .el-tabs__nav-wrap{
    background-color: #F2F5FF;
  }

  .el-tabs--card>.el-tabs__header .el-tabs__nav{
    border: none;

    .el-tabs__item{
      border: none;
      margin-right: 2px;
    }
  }

  & .el-tabs__item + .is-active,
  & .el-tabs__item.is-active{
    background-color: #ffffff;
    color: #3e6df6;
  }

  .el-tabs__content{
    height: calc(100% - 56px);
  }

  & .income-pane {
    height: 100%;
    padding: 0 20px;
  }

  & .income-contrast {
    height: 100%;
    font-size: 14px;

    & .income-template {
      height: 100%;

      .income-item-title{
        width: 50%;
        height: 40%;
        min-height: 215px;
        float: left;
      }

      ul {
        padding:20px 0 0 0;
        margin:0;
        color: #ffffff;
        list-style:none;
      }

      li{
        font-size: 12px;
        height: 50px;
        width: 150px;
        border-radius: 45px;
        display: inline-table;
        margin: 0 10px;
        padding: 15px;
        line-height: 20px;
        background:linear-gradient(94deg,rgba(174,197,255,1) 0%,rgba(139,171,253,1) 100%);
        box-shadow:0px 0px 5px 0px rgba(204,204,204,0.2);
        border-radius:4px;

        & span{
          font-size: 16px;
          font-weight: bold;
          display: block;
        }

        .yuan:after{
          content: '元';
          font-size: 12px;
          margin-left: 10px;
        }

        .percentage-second{
          display: inline;
        }

        .percentage:after{
          content: '%';
          font-size: 12px;
          margin-left: 8px;
          margin-right: 8px;
        }
      }

      .li-bg{
        background:linear-gradient(90deg,rgba(162,166,255,1) 0%,rgba(139,147,254,1) 100%);
        box-shadow:0px 0px 5px 0px rgba(204,204,204,0.2);
      }

      .template-ringchart{
        float: right;
        width: 50%;
        height: 40%;
        position: relative;

        .tabs-jd-item{
          display: flex;
          cursor: pointer;

          & div:nth-child(1){
            margin-right: 5px;
          }

          & div:nth-child(2){
            margin-left: 5px;
          }

          .tabs-jd-active{
            color: #3f6aff;
          }
        }
      }

      .template-card-chart{
        float: left;
        width: 40%;
        height: 40%;
        position: relative;
        text-align: center;

        .card-all{
          color: #89a2ff;
          display: inline-block;
          text-indent: 20px;
          font-size: 18px;
          font-weight: bold;

          &:after{
            content: '个';
            font-size: 12px;
            font-weight: normal;
          }
        }
      }

      .template-card-use{
        float: left;
        width: 50%;
        height: 60%;
        padding-right: 25px;
        padding-bottom: 10px;
        position: relative;
      }

      .template-stackchart {
        float: left;
        width: 50%;
        height: 60%;
        max-height: calc(100% - 215px);
        padding-bottom: 10px;

        & .chart {
          margin-top: 5px;
        }
      }

      & .income-ipass-title{
        width: 100%;
      }
    }
  }
}
