.guide-wapper {
  width: 100%;
  height: calc(100% - 40px);

  & .el-tabs__header {
    margin: 0 !important;
  }

  & .el-tabs__content {
    height: 100%;
    padding: 15px;
    background-color: #f2f5ff;
  }

  & .g {
    color: #878ca9;
  }

  & .b {
    font-weight: bold;
    color: #45a59a;
  }

  & .amap-guide-multi {
    position: absolute;
    top: 182px;
    left: 200px;
    width: 390px;
    height: 390px;
  }

  & .concise {
    width: 1200px;
    height: 578px;
    position: relative;

    & .small-screen {
      position: absolute;
      left: 142px;
      top: 120px;
      width: 182px;
      height: 254px;
      transform-style: preserve-3d;
      transform: rotateZ(-1deg);
      background: linear-gradient(
        74deg,
        rgba(3, 20, 27, 1) 0%,
        rgba(32, 95, 109, 1) 100%
      );
      color: #ffffff;
      font-size: 12px;
      z-index: 10;
    }

    & .concise-small {
      background: transparent;
      padding: 15px 0 0 15px;

      & .going-to {
        color: #54f1fb;
        margin-top: 25px;
        width: 160px;
        display: flex;
        height: 18px;
        line-height: 18px;
      }

      & .going-to > div {
        margin: 0 auto;
      }

      & .going-to div:nth-child(1) {
        background: #17434e;
        width: 80px;
        height: 18px;
        line-height: 18px;
        text-align: center;
      }

      & .going-to div:nth-child(2) {
        border: 1px solid #54f1fb;
        width: 38px;
        height: 18px;
        line-height: 18px;
        text-align: center;
        border-radius: 5px;
      }

      & .coming {
        font-size: 14px;
        margin-top: 12px;
      }

      & .arrival {
        color: #54f1fb;
        margin-top: 20px;
        margin-bottom: 8px;
      }

      & .next,
      & .arrival-time {
        font-size: 14px;
      }

      & .arrival-time::after {
        content: ' 分钟';
      }
    }

    & .concise-bg {
      width: 1200px;
      height: 578px;
      position: absolute;
      top: 0;
      left: 0;
      z-index: 99;
    }

    .big-screen {
      position: absolute;
      right: 342px;
      top: 70px;
      width: 283px;
      height: 480px;
      background-color: #ffffff;
      color: #000000;
      font-size: 12px;
      z-index: 10;

      & .amap-guide {
        width: 100%;
        height: 40%;
      }

      & .line-item {
        width: 100%;
        height: 60%;
        box-sizing: border-box;
        padding: 0 10px;
        font-size: 6px;

        & .item1,
        & .item2 {
          padding-bottom: 5px;
          box-sizing: border-box;
          box-shadow: 0 0 10px #defcff;
        }

        & .line1,
        & .line2 {
          height: 40px;
        }

        & .line1-site,
        & .line2-site {
          display: flex;
          padding: 0 5px;
        }

        & .site-item {
          width: 12px;
          margin: 0 auto;
        }
      }
    }
  }

  & .multi {
    position: relative;
    width: 1250px;
    box-sizing: border-box;
    overflow: hidden;
    background-color: #ffffff;
    padding-bottom: 10px;

    .multi-title {
      margin: 6px 0 0 10px;
      width: 100%;
      height: 25px;
      font-size: 14px;

      & div:nth-child(1) {
        width: 90px;
        text-align: center;
        line-height: 25px;
        float: left;
      }

      & div:nth-child(2) {
        width: 90px;
        float: left;
        margin-left: 20px;
        text-align: center;
        line-height: 25px;
        height: 25px;
        border-radius: 15px;
        border: 1px solid #3f6aff;
        color: #3f6aff;
      }
    }

    & .small-screen {
      position: absolute;
      left: 170px;
      top: 38px;
      width: 780px;
      height: 92px;
      background: linear-gradient(
        90deg,
        rgba(8, 38, 51, 1) 0%,
        rgba(26, 72, 85, 1) 50%,
        rgba(8, 38, 51, 1) 100%
      );
      color: #ffffff;
      font-size: 14px;
    }

    & .concise-small {
      padding: 20px;

      & .going-to {
        color: #54f1fb;
        margin-top: 12px;
        padding-right: 10px;
        display: flex;
        height: 24px;
        line-height: 24px;
        font-size: 14px;
        float: left;
        box-sizing: border-box;
      }

      & .going-to > div {
        margin: 0 auto;
      }

      & .going-to div:nth-child(1) {
        background: #17434e;
        padding: 0 20px;
        margin: 0 20px;
        height: 24px;
        line-height: 24px;
        text-align: center;
      }

      & .going-to div:nth-child(2) {
        border: 1px solid #54f1fb;
        padding: 0 10px;
        height: 24px;
        line-height: 24px;
        text-align: center;
        border-radius: 12px;
      }

      & .coming {
        font-size: 20px;
        margin: 12px 50px 0 30px;
        float: left;
      }

      & .arrival {
        color: #54f1fb;
        margin-top: -12px;
      }

      & .arrival2 {
        margin-top: -47px;
        float: left;
        margin-left: 570px;
      }

      & .next,
      & .arrival-time {
        font-size: 20px;
        margin-top: 8px;
      }

      & .arrival-time::after {
        content: ' 分钟';
      }

      & .next2 {
        float: left;
        margin-top: -25px;
        margin-left: 570px;
      }
    }

    & .big-screen {
      position: relative;
      top: 108px;
      left: 170px;
      width: 780px;
      height: 436px;
      background-color: #ffffff;
      color: #000000;
      font-size: 18px;

      & .line-item {
        width: 50%;
        height: 100%;
        box-sizing: border-box;
        padding: 0 10px;
        font-size: 6px;
        float: left;
        margin-left: 50%;
        font-size: 12px;

        & .item1,
        & .item2 {
          padding: 5px 0;
          box-sizing: border-box;
          box-shadow: 0 0 15px #defcff;
          margin-bottom: 10px;
        }

        & .line1,
        & .line2 {
          height: 65px;
        }

        & .line1-site,
        & .line2-site {
          display: flex;
          padding: 0 5px;
        }

        & .site-item {
          width: 12px;
          margin: 0 auto;
        }
      }
    }
  }

  & .multi-bottom {
    position: relative;
    margin-top: 72px;
    width: 840px;
    margin-left: 110px;
  }

  & .multi-bottom div:nth-child(1) {
    margin-left: 100px;
    font-size: 14px;
    float: left;
    font-weight: bold;
    margin-top: 15px;
  }

  & .multi-bottom div:nth-child(2) {
    margin-left: 10px;
    font-size: 14px;
    font-weight: bold;
    float: left;
    font-size: 22px;
    margin-top: 10px;
    color: #45a699;
  }

  & .multi-bottom .scan {
    float: right;
    margin-top: 32px;
    margin-right: 10px;
    font-size: 14px;
  }

  & .multi-bottom img {
    float: right;
    width: 65px;
    height: 65px;
    margin-top: -16px;
    margin-right: 20px;
  }

  & .multi-right {
    width: 270px;
    height: 556px;
    position: absolute;
    top: 38px;
    left: 956px;

    & .multi1 {
      width: 100%;
      height: 65%;
      float: left;
    }

    & .multi2 {
      width: 49%;
      height: 35%;
      float: left;
    }

    & .multi3 {
      width: 49%;
      height: 35%;
      float: left;
      margin-left: 2%;
    }
  }
}

.amap-logo {
  display: none !important;
}

.amap-copyright {
  display: none !important;
}

.image-preview {
  position: fixed;
  width: 100vw;
  height: 100vh;
  top: 0;
  bottom: 0;
  right: 0;
  cursor: default;
  text-align: center;
  background-color: rgba(0, 0, 0, 0.5);
  align-items: center;
  padding-top: 100px;
  z-index: 10000;

  .close {
    position: absolute;
    height: 30px;
    width: 30px;
    margin-left: calc(50% + 472px);
    margin-top: -5px;
  }

  img {
    width: 1000px;
    height: 571px;
  }
}
