.zhe{
  width: 100vw;
  height: 100vh;
  background: rgba(0,0,0,0.5);
  position:fixed;
  left: 0;
  top: 0;
  z-index: 2000;
}
.zhe_body{
  width: 20%;
  height: 40%;
  background: #fff;
  position: relative;
  left: 40%;
  top: 30%;
  border-radius: 10px;
  padding-top: 1%;
}
.zhe_body_header{
  margin-left: 5%;
