.about {
  /* width: 100vw; */
  height: 100vh;
  background: #122f7e;
  font-size: 13px;
}
/* 地图 */

/* 监视 */
.cctv {
  float: right;
  width: 47%;
  height: 85%;
  margin-right: 1.5%;
  border: 3px solid #6edcfa;
  box-shadow: 0 0 10px #ffffff;
}
/* 头部标签 */
.header {
  height: 8%;
  width: 100%;
  margin-bottom: 10px;
  background-image: url("../../assets/yingzhi/topback.png");
  background-repeat: no-repeat;
  background-size: 100%;
}
.daoy {
  position: relative;
  left: 20px;
  top: 5px;
  z-index: 1000000;
}
.header_text {
  width: 30%;
  text-align: center;
  color: #fff;
  line-height: 50px;
  font-size: 20px;
  letter-spacing: 10px;
  float: left;
  position: absolute;
  left: 50%;
  top: 0%;
  margin-left: -15%;
}

button {
  border-radius: 5px;
  background: #1f3a89;
  height: 25px;
  outline: none;
  padding: 0 10px 0 10px;
  border: 1px solid #fff;
  color: #6fdafc;
}
.ze,
.sh {
  border-radius: 5px;
  background: #1f3a89;
  width: 110px;
  height: 25px;
  outline: none;
  border: 1px solid #66cdee;
  color: #6fdafc;
  margin-top: 10px;
  margin-left: 50px;
}
.ze,
.sh:hover {
  background: #fff;
  color: black;
  border: 1px solid #fff;
}
.cun,
.jixu {
  border-radius: 5px;
  background: #1f3a89;
  width: 20%;
  height: 25px;
  outline: none;
  border: 1px solid #66cdee;
  color: #6fdafc;
  margin-top: 50px;
  margin-left: 20%;
}
.ze,
.sh:hover {
  background: #fff;
  color: black;
  border: 1px solid #fff;
}

#container {
  width: 69%;
  height: 85%;
  margin: 13px;
  position: absolute;
  bottom: 15px;
}
.dian {
  width: 100px;
  height: 80%;
  position: absolute;
  left: 1.6%;
  top: 17%;
  z-index: 9999;
  cursor: pointer;
}
.hengd {
  width: 90px;
  height: 25px;
  border-radius: 0 100px 100px 0;
  margin-bottom: 10px;
  margin-top: 10px;
  color: #fff;
  font-size: 13px;
  line-height: 25px;
  text-align: right;
  padding-right: 10px;
  z-index: 9999;
  background: #1e3b88;
}
.hengd div {
  padding-right: 20px;
}
.hengd2 div {
  background: rgba(00, 00, 00, 0.5);
  width: 220%;
  height: 25px;
  margin-top: -25px;
  z-index: 9999;
  border-radius: 0 100px 100px 0;
  display: block;
  animation: dong1 0.5s;
  overflow: hidden;
}
@keyframes dong1 {
  0% {
    width: 0%;
    color: #000;
  }
  100% {
    width: 220%;
    color: #fff;
  }
}
.hengd1 div {
  display: none;
}
button:hover {
  background: #151748;
  color: #fefffb;
}
.right {
  background: #151748;
  width: 29.2%;
  height: 85.2%;
  z-index: 9999;
  float: right;
  position: absolute;
  right: 10px;
  bottom: 27px;
}
.right div {
  width: 80%;
  margin-left: 10%;
  overflow: hidden;
  display: block;
  margin-top: 5%;
  margin-bottom: 1%;
}
.right input,
select {
  float: right;
  max-width: 60%;
}
.right p {
  float: left;
  padding-left: 30px;
  margin: 0;
}
.right .ipt {
  margin-top: 10px;
  max-width: 80%;
  margin-left: 10%;
  height: 30px;
  width: 80%;
  border-radius: 8px;
  background: #43456c;
  outline: none;
  border: 1px solid #43456c;
  color: #898c95;
  padding-left: 15px;
  float: none;
  /* margin-top: 10px;
  margin-left: 20px; */
}
.ipt1 {
  height: 20px;
  width: 59%;
  float: right;
  background: #fff;
  outline: none;
  border: 1px solid #fff;
  color: #898c95;
  padding-left: 15px;
}
.ipt2 {
  height: 80px;
  width: 59%;
  float: right;
  background: #fff;
  outline: none;
  border: 1px solid #fff;
  color: #898c95;
  padding-left: 15px;
  border-radius: 5px;
}
.ipt3 {
  height: 20px;
  width: 59%;
  float: right;
  background: #fff;
  outline: none;
  border: 1px solid #fff;
  color: #898c95;
  padding-left: 15px;
}
.shanc {
  position: fixed;
  left: 50%;
  top: 50%;
  width: 300px;
  height: 200px;
  background: #151748;
  z-index: 99999;
  margin-left: -150px;
  margin-top: -100px;
  color: #fff;
}
.shanc_an {
  float: left;
  width: 20%;
  margin-left: 20%;
  background: #fff;
  color: #000;
  cursor: pointer;
  text-align: center;
  border-radius: 5px;
  height: 30px;
  line-height: 30px;
}
.shanc p {
  width: 100%;
  height: 150px;
  line-height: 150px;
  text-align: center;
  font-weight: 800;
}
