result:
.role-container{
  height: 450px;
  color: #1D2133;

  .bg{
    border-radius: 6px;
    padding: 30px;
    height: 100%;
  }

  p{
    margin: 0;
  }

  .title{
    font-size: 16px;
    font-weight: 550;
  }

  .f-l{
    float: left;
  }

  .f-r{
    float: right;
  }

  .role-wapper{
    width: 100%;
    height: 100%;
    background-color: #ffffff;
  }

  .role-top{
    height: 30px;
  }

  .first-row{
    height: calc(100% - 40px);
    margin-top: 15px;

    .company{
      height: 97%;
      margin-top: 1.5%;
      box-shadow:0px 0px 20px 0px rgba(63,106,255,0.15);

      .auth-tree-title {
        text-indent: 27px;
        position: relative;
        font-size: 16px;
        font-weight: 550;
      }

      .auth-tree-title::before {
        content: '';
        position: absolute;
        left: 0;
        width: 7px;
        height: 16px;
        background: rgba(63, 106, 255, 1);
        border-radius: 4px;
      }
    }

    .function-table{
      width: 100%;
      height: 98%;
      position: relative;
    }
  }

  .company-tree{
    height: calc(100% - 50px);
    margin-top: 30px;
    overflow-y: auto;
  }

  .company-info-btn{
    margin-top: -8px;
  }
}
