import VueAMap from 'vue-amap'
import { post } from '@/api/axios'
var amapManager = new VueAMap.AMapManager()
export default {
  name: 'Map',
  data() {
    return {
      disabled: false,
      tank: '',
      currentRow: '',
      name: '',
      bei: '',
      zoom: 16,
      center: [116.336496, 39.942178],
      mapStyle1: 'amap://styles/whitesmoke',
      activeName: 'qing',
      tape: 1,
      amapManager,
      events: {},
      overlayGroups: [],
      data: [], // table 列表数据集合
      rowindex: '',
      dataid: 0,
      options: [
        {
          value: '1',
          label: '游览路线'
        },
        {
          value: '2',
          label: '巡逻路线'
        }
      ],
      value: '',
      options2: [
        {
          value: '1',
          label: '单兵'
        },
        {
          value: '2',
          label: '对讲机'
        }
      ],
      value2: '',
      line: ''
    }
  },
  computed: {
  },
  watch: {
    data() {
    },
    disabled() {
      if (!this.disabled) {
        this.data = []
        this.name = []
        this.bei = []
        this.addmarker()
      }
    }
  },
  created() {
    this.addfun()
  },
  methods: {
    ti() {
      if (this.name !== '' && this.data.length > 0) {
        var markers = []
        for (var i = 0; i < this.data.length; i++) {
          markers.push({
            pointName: this.data[i].name,
            circuitName: this.name,
            orderNumber: i + 1,
            circuitPointLon: this.data[i].indt[0],
            circuitPointLat: this.data[i].indt[1]
          })
        }
        var data = {
          cpid: this.$store.state.user.companyId,
          circuitTypeId: 3,
          circuitType: '游玩',
          circuitName: this.name,
          remark: this.bei,
          circuitpointlist: markers
        }
        post('/mngCircuitInfo/savemngcircuitinfo', data).then(res => {
          this.disabled = !this.disabled
          // that.attractions = res.data
          this.$message({
            message: '保存成功',
            type: 'success'
          })
          // setTimeout(() => {
          //   this.tank = ''
          //   this.$router.push({ path: '/maps/linjingxi' })
          // }, 2000)
        })
      } else {
        this.$message({
          message: '请认真检查填写项',
          type: 'warning'
        })
      }
    },
    ti2() {
      if (this.name !== '' && this.value !== '') {
        var markers = []
        for (var i = 0; i < this.data.length; i++) {
          markers.push({
            pointName: this.data[i].name,
            circuitName: this.name,
            orderNumber: i + 1,
            circuitPointLon: this.data[i].indt[0],
            circuitPointLat: this.data[i].indt[1]
          })
        }
        var data = {
          cpid: this.$store.state.user.companyId,
          circuitTypeId: 3,
          circuitType: '游玩',
          circuitName: this.name,
          remark: this.bei,
          circuitpointlist: markers
        }
        post('/mngCircuitInfo/savemngcircuitinfo', data).then(res => {
          // that.attractions = res.data
          this.$message({
            message: '保存成功',
            type: 'success'
          })
          this.name = ''
          this.bei = ''
          this.data = []
          this.addmarker()
        })
      } else {
        this.$message({
          message: '请认真检查填写项',
          type: 'warning'
        })
      }
    },
    addfun() {
      var that = this
      this.events = {
        init(o) {
          o.on('click', function(e) {
            that.wei(e.lnglat.getLng(), e.lnglat.getLat(), that)
          })
        }
      }
    },
    addmarker() {
      console.log(123)
      var that = this
      var o = amapManager.getMap()
      var markers = []
      var open = []
      o.remove(that.overlayGroups)
      o.remove(that.line)
      for (var i = 0; i < that.data.length; i++) {
        if (that.data[i].indt !== '') {
          var marker = new AMap.Marker({
            position: that.data[i].indt,
            rowindex: that.rowindex,
            text: '123',
            icon: 'https://webapi.amap.com/theme/v1.3/markers/n/mark_b' + (markers.length + 1) + '.png'
          })
          open.push(that.data[i].indt)
          markers.push(marker)
        }
      }
      if (open.length > 1) {
        console.log(open)
        that.line = new AMap.Polyline({
          map: o,
          strokeColor: '#80d8ff',
          isOutline: true,
          strokeStyle: 'dashed',
          outlineColor: 'white',
          path: open
        })
      }
      this.overlayGroups = new AMap.OverlayGroup(markers)
      o.add(that.overlayGroups)
    },
    wei(a, b, that) {
      console.log(that.rowindex)
      if (that.rowindex !== '') {
        that.data[that.rowindex].indt = [a, b]
      } else {
        that.data[that.dataid].indt = [a, b]
      }
      this.addmarker()
    },
    handleClick(tab, event) {
      console.log(tab, event)
    },
    jia() {
      this.data.push({ name: '', indt: '' })
      this.dataid = this.data.length - 1
      this.rowindex = this.dataid
      this.$refs.singleTable.setCurrentRow(this.data[this.dataid])
    },
    datafun(i) {
      console.log(i)
      this.dataid = i
    },
    jian() {
      if (this.rowindex || this.rowindex === 0) {
        this.data.splice(this.rowindex, 1)
      } else {
        this.data.splice(this.dataid, 1)
      }
      this.rowindex = this.data.length - 1
      this.$refs.singleTable.setCurrentRow(this.data[this.data.length - 1])
      this.addmarker()
    },
    shang() {
      if (this.dataid > 1 || this.dataid === 1) {
        var a = this.data[this.dataid]
        this.data[this.dataid] = this.data[this.dataid - 1]
        this.data[this.dataid - 1] = a
        this.dataid -= 1
        this.$refs.singleTable.setCurrentRow(this.data[this.dataid])
      }
      this.addmarker()
    },
    xia() {
      console.log(this.rowindex)
      console.log(this.dataid)
      if (this.dataid !== this.data.length - 1 && this.data.length > 1) {
        var a = this.data[this.dataid]
        this.data[this.dataid] = this.data[this.dataid + 1]
        this.data[this.dataid + 1] = a
        this.dataid += 1
        this.$refs.singleTable.setCurrentRow(this.data[this.dataid])
      }
      this.addmarker()
    },
    indexMethod(index) {
      return index + 1
    },
    tableRowClassName({ row, rowIndex }) {
      row.index = rowIndex // 把每一行的索引放进row
    },
    onRowClick(row, event, column) {
      var index = row.index
      this.rowindex = index // 点击当前行的行数
      this.dataid = index
      console.log(this.data)
    }
  }
}
