.zjcb-container{
  background: #f0f3ff;
  width: 100%;
  height: calc(100vh - 80px);
  overflow: hidden;
  position: relative;
  padding: 1%;
}
.zjcb_header{
  width: 100%;
  height: 10%;
  background: #ffffff;
  padding: 1%;
  margin-bottom: 1%;
  border-radius: 8px;
  border-left: 8px solid #3F6AFF;
}
.span{
  margin-left: 10px;
  margin-right: 10px;
}
.input{
  width: 15%;
  display: inline-block;
}
/* 按钮 */
.bai{
  float: right;
  padding: 8px;
  width: 5%;
  text-align: center;
  border:1px solid rgba(63,106,255,1);
  color: #3F6AFF;
  font-size: 10px;
  border-radius:15px;
  margin-right: 3%;
  margin-top: 0.3%;
}
.btn{
  padding: 8px;
  width: 5%;
  text-align: center;
  background:linear-gradient(90deg,rgba(63,106,255,1) 0%,rgba(109,142,255,1) 100%);
  box-shadow:0px 3px 8px 0px rgba(63,106,255,0.3);
  border-radius:15px;
  font-size: 10px;
  float: right;
  color: #FFFFFF;
  margin-right: 3%;
  margin-top: 0.3%;
}
/* 主体 */
.zjcb_body{
  width: 100%;
  height: 87.5%;
  border-radius: 8px;
}
/* 左边 */
.body_left{
  float: left;
  width: 100%;
  height: 100%;
  background: #FFFFFF;
  border-radius: 8px;
  padding: 1%;
}
.left_header{
  overflow: hidden;
  margin-bottom: 3%;
}
.left_header div{
  width: 8%;
  height: 5vh;
  background: rgba(241,244,253,1);
  border:1px dashed rgba(160,181,255,1);
  float: left;
  margin-right: 10px;
  text-align: center;
  line-height: 5vh;
  text-indent: 10px;
  color: #3F6AFF
}
.left_body{
  width: 100%;
  height: 85%;
  background:rgba(247,248,255,1);
}
/* 右边 */
.body_right{
  float: right;
  width: 24%;
  height: 100%;
  background: #FFFFFF;
  border-radius: 8px;
  padding: 1%;
}
.left_header .zhong{
  background: #3F6AFF;
  color: #ffffff;
}
.est2{
  position: absolute;
  left: 0;
  top: 0;
}
