.traffic {
  background: #f0f3ff;
  width: 100%;
  height: calc(100vh - 80px);
  overflow: hidden;
}
/* 头部 */
.header {
  width: 100%;
  height: 3%;
  background: #ffffff;
}
/* 4大块 */
.left1 {
  float: left;
  margin-left: 1%;
}
.top1 {
  width: 48.5%;
  height: 45%;
  background: rgba(255, 255, 255, 1);
  box-shadow: 0rem 0rem 1rem 0rem rgba(63, 106, 255, 0.08);
  border-radius: 0rem;
  margin-top: 1%;
}
.bottom {
  width: 48.5%;
  height: 46%;
  background: rgba(255, 255, 255, 1);
  box-shadow: 0rem 0rem 1rem 0rem rgba(63, 106, 255, 0.08);
  border-radius: 0rem;
  margin-top: 1%;
}
.right {
  float: right;
  margin-right: 1%;
}
/* 数据分析 */
.lefttop_header {
  height: 1rem;
  width: 100%;
  font-size: 0.8rem;
  text-indent: 10px;
  padding-top: 10px;
  font-family: PingFang SC;
  font-weight: 500;
  color: rgba(29, 33, 51, 1);
}
.visiting {
  float: left;
  width: 50%;
  height: 100%;
  padding-top: 2rem;
  margin-left: 8%;
}
.instrument {
  float: left;
  width: 40%;
  height: 100%;
}
/* 下拉框 */
.sel {
  width: 60px;
  height: 30px;
  font-size: 0.8rem;
  text-indent: 10px;
  appearance: none;
  -moz-appearance: none;
  -webkit-appearance: none;
  appearance: none;
  background: url("../../assets/shu/xia.png") no-repeat scroll right 5px center
    transparent;
  background-color: rgba(63, 106, 255, 0.12);
  border: 0px solid rgba(63, 106, 255, 1);
  border-radius: 0rem;
  position: absolute;
  float: left;
  left: 30%;
  top: 20px;
  padding-right: 10px;
  z-index: 10;
}
/* 四个车位显示 */
.visiting div {
  width: 48%;
  height: 30%;
  font-size: 0.8rem;
  box-shadow: 0rem 0rem 1rem 0rem rgba(204, 204, 204, 0.2);
  border-radius: 0.5rem;
  padding-left: 2rem;
  font-family: PingFang SC;
  font-weight: 500;
  color: rgba(255, 255, 255, 1);
  line-height: 300%;
  margin-top: 5%;
}
.visiting_div1 {
  float: left;
  background: linear-gradient(
    94deg,
    rgba(174, 197, 255, 1) 0%,
    rgba(139, 171, 253, 1) 100%
  );
}
.visiting_div2 {
  float: right;
  background: linear-gradient(
    94deg,
    rgba(174, 197, 255, 1) 0%,
    rgba(139, 171, 253, 1) 100%
  );
}
.visiting_div3 {
  float: left;
  background: linear-gradient(
    90deg,
    rgba(162, 166, 255, 1) 0%,
    rgba(139, 147, 254, 1) 100%
  );
}
.visiting_div4 {
  float: right;
  background: linear-gradient(
    90deg,
    rgba(162, 166, 255, 1) 0%,
    rgba(139, 147, 254, 1) 100%
  );
}
.visiting div span {
  font-size: 3vh;
}
