.about {
  width: 100%;
  height: 100%;
  position: fixed;
  top: 0;
  left: 0;
  background-image: url("../../../assets/yingzhi/bg_img.png");
  background-repeat: no-repeat;
  background-size: 100%;
}
/* 头部标签 */
.header {
  height: 8%;
  width: 100%;
  margin-bottom: 10px;
  background-image: url("../../../assets/yingzhi/topback.png");
  background-repeat: no-repeat;
  background-size: 100%;
}
.daoy {
  position: relative;
  left: 20px;
  top: 5px;
  z-index: 999;
}
.header_text {
  width: 30%;
  text-align: center;
  color: #fff;
  line-height: 50px;
  font-size: 20px;
  letter-spacing: 10px;
  float: left;
  position: absolute;
  left: 50%;
  top: 0%;
  margin-left: -15%;
}
.echarts_box{
  width: 100%;
  height: calc(100% - 8%);
  padding: 0 23px 30px 23px;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
}
.echarts_box .top ,.echarts_box .bottom{
  width: 100%;
  height: calc((100% - 10.5px)/2);
  display:flex;
  justify-content: space-between;
}
.echarts_box .top .top_left, .echarts_box .top .top_right, .echarts_box .bottom .bottom_left,  .echarts_box .bottom .bottom_right{
  position: relative;
  width: calc((100% - 25.5px)/2);
  padding: 15px;
}
.echarts_box .top .top_left .echart_top_left{
  height: 100%;
}
.title{
  width: 100%;
  height: 16px;
  line-height: 16px;
  text-align: center;
  font-size:16px;
  font-family:Adobe Heiti Std;
  font-weight: 400;
  color:rgba(255,252,1,1);
  margin-top: 12.5px;
}
.topleft, .topright, .bottomleft, .bottomright{
  width: 100%;
  height: calc(100% - 28.5px);
}
