.home-wrapper{
  height: 100%;
  width: 100%;

  & .home-carousel{
    position: absolute;
    margin-top: 50px;
    height: calc(100vh - 50px);
    width: 100%;
    left: 0;
    right: 0;
    bottom: 0;

    .el-carousel__container{
      height: 100%;

      img{
        height: 100%;
        width: 100%;
      }

      .el-carousel__item{
        text-align: center;
      }
    }

    .carousel1-wrapper{
      position: absolute;
      top: 0;
      left: 0;
      right: 0;
      margin: 120px auto 0;
      color: #fff;

      .carousel1-text{
        width: 449px;
        height: 94px;
      }

      .property{
        margin-top: 100px;

        ul,li{
          padding:0;
          margin:0;
          list-style:none;
        }

        li{
          font-size: 12px;
          height: 180px;
          width: 90px;
          padding-top: 40px;
          border-radius: 45px;
          display: inline-block;

          & .svg-icon{
            height: 30px;
            width: 30px;
            margin-bottom: 22px;
          }

          & span{
            height: 18px;
            display: block;
          }

          & .li-title{
            font-size: 14px;
            height: 24pxs;
          }
        }

        li:nth-child(2){
          margin: 0 15%;
        }

        li:hover{
          background-color: #554AE5;
        }
      }
    }

    .carousel2-wrapper{

      .carousel2-text{
        position: absolute;
        width: 250px;
        height: auto;
        left: 15%;
        top:  50%;
        transform: translate(0, -50%);
      }

      .carousel2-img{
        position: absolute;
        width: 55%;
        height: auto;
        right: 2%;
        top:  50%;
        transform: translate(0, -50%);
      }
    }
  }
}
