import { post, get } from '@/api/axios'
export default {
  name: 'Broadcast',
  data() {
    return {
      fileList: [],
      // 分页
      lx: '',
      bc: '',
      zdl: '',
      mes: '',
      pagesize: 1,
      pageNumber: 10,
      arrdata: [],
      fudata: {
        cpid: this.$store.state.user.companyId
      },
      //    分页
      all: 0, //      总页数
      cur: 1,
      mingc: '',
      ip: '',
      type: '',
      data: {},
      multipleSelection: []
    }
  },
  computed: {
  },
  watch: {
  },
  mounted() {
    this.shua()
  },
  methods: {
    handleSelectionChange(val) {
      this.multipleSelection = val
    },
    btnClick(data) {
      console.log(data)
      this.cur = data
      this.shua()
    },
    shua() {
      const that = this
      get('/mngradio/getradiofile', { cpid: this.$store.state.user.companyId, pageNumber: that.cur - 1, pageSize: 10 }).then(res => {
        console.log(123, res)
        that.arrdata = res.data.content
        that.all = res.data.numberOfElements
      })
    },
    updata(data) {
      var formData = new FormData()
      formData.append('file', data)
      post('/mngradio/saveradiofile/' + this.$store.state.user.companyId, formData).then(res => {
        console.log(res)
        this.shua()
      })
      return false
    },
    delet() {
      const that = this
      var ids = ''
      for (var i = 0; i < that.multipleSelection.length; i++) {
        if (i === 0) {
          ids += that.multipleSelection[i].id
        } else {
          ids += ',' + that.multipleSelection[i].id
        }
      }
      get('/mngradio/delradiofile', { ids: ids }).then(res => {
        console.log(res.msg)
        if (res.msg === 'SUCCESS') {
          this.shua()
        } else {
          this.$message({
            message: res.msg,
            type: 'warning'
          })
        }
      })
    },
    alldelet() {
      var ids = ''
      const that = this
      for (var i = 0; i < that.arrdata.length; i++) {
        if (i === 0) {
          ids += that.arrdata[i].id
        } else {
          ids += ',' + that.arrdata[i].id
        }
      }
      get('/mngradio/delradiofile', { ids: ids }).then(res => {
        console.log(res)
        this.shua()
      })
    }
  }
}
