.about {
  width: 100%;
  height: 100%;
  position: fixed;
  top: 0;
  left: 0;
  background-image: url("../../../assets/yingzhi/bg_img.png");
  background-repeat: no-repeat;
  background-size: 100%;
}
/* 头部标签 */
.header {
  height: 8%;
  width: 100%;
  margin-bottom: 10px;
  background-image: url("../../../assets/yingzhi/topback.png");
  background-repeat: no-repeat;
  background-size: 100%;
}
.daoy {
  position: relative;
  left: 20px;
  top: 5px;
  z-index: 999;
}
.header_text {
  width: 30%;
  text-align: center;
  color: #fff;
  line-height: 50px;
  font-size: 20px;
  letter-spacing: 10px;
  float: left;
  position: absolute;
  left: 50%;
  top: 0%;
  margin-left: -15%;
}
.echarts_box{
  width: 100%;
  height: calc(100% - 8%);
  padding: 0 23px 30px 23px;
  display: flex;
  flex-flow: row nowrap;
  justify-content: space-between;
}
/* 页面结构  left--左 center--中间 right--右*/
.echarts_box .left, .echarts_box .right{
  width: calc((100% - 20px) * 0.31);
  height: 100%;
}
.echarts_box .center_echarts {
  width: calc((100% - 20px) * 0.35);
  height: 100%;
  background: rgba(20, 23, 71, 0.5);
  padding: 0 20px 20px 20px;
  border-radius: 10px;
}
.map_box{
  width: 100%;
  height: calc(100% - 26px - 15px);
  margin-top:15px;
  border-radius: 10px;
  position: relative;
}
.map_box ul {
  position: absolute;
  top:10px;
  left: 10px;
  list-style: none;
  margin: 0;
  padding:0px;
  z-index: 100;
}
.map_box ul li{
  width: 90px;
  height: 25px;
  border-radius: 0 100px 100px 0;
  margin-bottom: 10px;
  margin-top: 10px;
  color: #fff;
  font-size: 13px;
  line-height: 25px;
  text-align: right;
  padding-right: 10px;
  z-index: 100;
  background: #1e3b88;
  cursor: pointer;
}
.defaults{
  position: relative;
}
.defaults div {
  padding-right: 20px;
}
.defaults img{
  position: absolute;
  left: 8px;
  top: 6px;
  width: 12px;
  height: 12px;
}
.defaults_2 div {
  background: rgba(00, 00, 00, 0.5);
  width: 220%;
  height: 25px;
  margin-top: -25px;
  z-index: 100;
  border-radius: 0 100px 100px 0;
  display: block;
  animation: dong1 0.5s;
  overflow: hidden;
}
@keyframes dong1 {
  0% {
    width: 0%;
    color: #000;
  }
  100% {
    width: 220%;
    color: #fff;
  }
}
.defaults_1 div {
  display: none;
}
.map_box .map{
  width: 100%;
  height: 100%;
}
/deep/.amap-logo{
  left: -200px!important;
}
/deep/.amap-copyright{
  left:-500px !important;
}
/* .left, .right{
  display: flex;
  flex-direction: column;
  justify-content: space-between;
} */
.left .left_top, .right .right_top {
  height: calc((100% - 20px)*0.4);
  width: 100%;
}
.left .left_center, .left .left_bottom, .right .right_center, .right .right_bottom{
  width: 100%;
  height: calc((100% - 20px)*0.3);
}
.title{
  width:100%;
  height: 18px;
  font-size: 16px;
  font-family:Adobe Heiti Std;
  font-weight:normal;
  color:rgba(255,252,1,1);
  text-align: center;
  line-height: 18px;
  margin-top: 10px;
}
/* 左结构  两个柱状图*/
.left_top {
  display: flex;
  flex-flow: row nowrap;
  justify-content: space-between;
  position: relative;
}
.left_top .left_top_left, .left_top .left_top_right{
  width: 50%;
}
.tab_table {
  width: 100%;
  height: calc(100% - 38px);
  margin-top: 10px;
  display: table;
}
.tab_table tr{
  width: 100%;
  font-size:12px;
  height: 13px;
  font-family:Adobe Heiti Std;
  color:rgba(255,255,255,1);
}
.tab_table tr:nth-child(1){
  font-weight: 400;
}
.tab_table tr td, .tab_table tr th{
  width: calc(100% / 7);
  max-width: 48px;
  word-break: keep-all; /* 不换行 */
  white-space: nowrap; /* 不换行 */
  overflow: hidden; /* 内容超出宽度时隐藏超出部分的内容 */
  text-overflow: ellipsis; /* 当对象内文本溢出时显示省略标记(...) ；需与
  overflow:hidden;一起使用。*/
}
.before_month tr td, .before_month tr th{
  width: calc(100% / 6);
  width: 60px;
  word-break: keep-all; /* 不换行 */
  white-space: nowrap; /* 不换行 */
  overflow: hidden; /* 内容超出宽度时隐藏超出部分的内容 */
  text-overflow: ellipsis; /* 当对象内文本溢出时显示省略标记(...) ；需与
  overflow:hidden;一起使用。*/
}
.tab_table tr td{
  text-align: center;
}
.right_top{
  position: relative;
  color: #fff;
}
.right_top .weather{
  width: 100%;
  height: calc((100% - 10px)/2);
  padding: 10px;
  position: relative;
}
.right_top .weather .temperature_times{
  width: 100%;
  height: 35px;
  position: relative;
  display: flex;
  flex-flow: row wrap;
}
.right_top .weather .temperature_times .temperature,
.right_top .weather .temperature_times .now_times{
  width: 50%;
  height:100%;
  display: inline-block;
}
.temperature img{
  width: 35px;
  height: 28px;
}
.temperature span{
  display: inline-block;
}
.temperature .weather_stadus {
  font-size:14px;
  font-family:SourceHanSansCN;
  font-weight:400;
  color:rgba(255,255,255,1);
}
.temp_number{
  font-size:35px;
  font-family:SourceHanSansCN;
  font-weight:bold;
  color:rgba(255,255,255,1);
}
.now_times li {
  font-size:14px;
  font-family:SourceHanSansCN;
  font-weight:400;
  color:rgba(255,255,255,1);
}
.now_times span{
  width: 100%;
  display: inline-block;
  text-align: center;
  font-size:18px;
  font-family:SourceHanSansCN;
  font-weight:bold;
  color:rgba(255,255,255,1);
}
.right_top .weather ul{
  list-style: none;
  margin: 0;
  padding: 0;
  display: flex;
  flex-flow: row nowrap;
  justify-content: space-between;
}
.list_weather {
  width: 100%;
  margin-top: 20px!important;
}
.list_weather li{
  position: relative;
  height: 15px;
  line-height: 15px;
  border-radius: 15px;
  width: calc((100% - 15px)/4);
  margin-right: 5px;
  background-color: #A0A0BF;
  font-size:8px;
  font-family:SourceHanSansCN;
  font-weight:400;
  color:rgba(255,255,255,1);
  text-align: center;
}
.list_weather li img{
  width:10px;
  height:10px;
  margin-right: 3px;
}
.right_top .real_time ul{
  list-style: none;
  margin: 0;
  padding: 0;
  display: flex;
  flex-flow: row wrap;
  justify-content: space-between;
}
.right_top .real_time{
  width: 100%;
  height: calc((100% - 10px)/2);
  margin-top: 10px;
}
.people_flow{
  position: relative;
  width: 100%;
  height: calc((100% - 38px));
  padding: 10px 30px 10px 30px;
  display: flex;
  flex-flow: row wrap;
  justify-content: flex-start;
  margin-top: 10px;
}
.people_flow ul li {
  text-align: left;
  width: 45%;
  font-size:9px;
  font-family:Adobe Heiti Std;
  font-weight:normal;
  color:rgba(0,160,233,1);
}
.people_flow ul li .numberof{
  font-size:12px;
  font-family:Adobe Heiti Std;
  font-weight:normal;
  color:#FFFC01;
}
