import { post } from '../../api/axios.js'
export default {
  data() {
    return {
      radio: '1',
      dialogImageUrl: '',
      name: '',
      data: {},
      arr: [],
      pickerOptions1: {
        shortcuts: [
          {
            text: '今天',
            onClick(picker) {
              picker.$emit('pick', new Date())
            }
          },
          {
            text: '昨天',
            onClick(picker) {
              const date = new Date()
              date.setTime(date.getTime() - 3600 * 1000 * 24)
              picker.$emit('pick', date)
            }
          },
          {
            text: '一周前',
            onClick(picker) {
              const date = new Date()
              date.setTime(date.getTime() - 3600 * 1000 * 24 * 7)
              picker.$emit('pick', date)
            }
          }
        ]
      },
      value1: '',
      value2: '',
      options: [
        {
          value: '选项1',
          label: 'A区停车场'
        },
        {
          value: '选项2',
          label: '景区'
        },
        {
          value: '选项3',
          label: 'C区旋转木马'
        }
      ],
      value: '',
      value11: 0,
      value12: 50,
      value13: 36,
      value14: 48,
      value15: 42
    }
  },
  methods: {
    formatTooltip(val) {
      return val / 100
    },
    qd() {
      post('/faceInfo/insert', {
        faceName: this.data.name,
        sex: this.radio,
        phone: this.data.name1,
        employeeNum: this.data.bh,
        department: this.data.bm,
        position: this.data.zw,
        certificateType: this.data.lx,
        certificateNum: this.data.zjbh,
        faceType: 0
      }).then(response => {
      //  console.log(response.msg == 'SUCCESS')
        if (response.msg === 'SUCCESS') {
          this.$notify({
            title: '成功',
            message: '保存成功',
            type: 'success',
            duration: 2000
          })
          this.$router.push({
            path: '/face/bmd'
          })
        } else {
          alert(response.msg)
        }
      })
    }
  }
}
