.tac {
  width: 100px;
}

.gr-message {
  width: 100%;
  height: 100%;
  font-size: 14px;
  background: #eff3ff;
  padding: 15px;
  box-sizing: border-box;
  overflow: hidden;

  .whitek {
    width: 100%;
    background: rgba(255, 255, 255, 1);
    box-shadow: 0px 0px 13px 0px rgba(63, 106, 255, 0.08);
    border-radius: 3px;
    padding: 30px;
  }

  .photo {
    height: 160px;
    width: 100px;
    text-align: center;

    .user-avatar {
      width: 100px;
      height: 100px;
      border-radius: 50%;
      vertical-align: middle;
    }
  }
  .text-under {
    text-decoration: underline;
    color: rgba(63, 106, 255, 1);
    cursor: pointer;
  }

  .zp .el-upload-dragger {
    height: 160px;
    width: 260px;
  }
  .information {
    text-indent: 27px;
    position: relative;
    font-size: 16px;
    font-weight: 500;
  }
  .information::before {
    content: '';
    position: absolute;
    left: 0;
    width: 7px;
    height: 16px;
    background: rgba(63, 106, 255, 1);
    border-radius: 4px;
  }
  .mes {
    display: flex;
  }
  .name {
    margin-left: 43px;
    color: rgba(29, 33, 51, 1);
  }
  .namea {
    margin-left: 56px;
    color: rgba(29, 33, 51, 1);

    p{
      height: 16px;
    }
  }
  .p-l {
    padding-left: 10px;
  }
  .dialog-password,
  .mobile-form{
    width: 100%;
    text-align: center;

    .el-form-item__label{
      float: none;
    }

    .el-form-item__label:before{
      content: '' !important;
      margin-right: 0 !important;
    }

    .el-form-item__content{
      display: inline-block;
      width: 200px;
    }
  }
  .dialog-password{
    text-align: right !important;
    padding-right: 21%;
  }
  .dialog-footer{
    width: 100%;
    display: flex;
    justify-content: space-around;
    padding: 0 15% 15px;
  }
}
