.news-container{
  background: #f0f3ff;
  width: 100%;
  height: calc(100vh - 80px);
  overflow: hidden;
  padding: 1%;
}
.backcha{
  background: #ffffff;
  height:100%;
  padding: 2%;
}
.el-row{
  height: 100%;
}
.el-col{
  height: 100%;
}
.btn{
  width: 10%;
  padding-top: 0.5%;
  padding-bottom:0.5%;
  background:linear-gradient(90deg,rgba(63,106,255,1) 0%,rgba(109,142,255,1) 100%);
  box-shadow:0px 0px 11px 0px rgba(63,106,255,0.28);
  border-radius:8px;
  text-align: center;
  font-size: 8px;
  color: #fff;
  float: left;
  margin-left: 1%;
}
.btn2{
  width: 10%;
  padding-top: 0.5%;
  padding-bottom:0.5%;
  background:#fff;
  border:1px solid rgba(63,106,255,1);
  box-shadow:0px 3px 8px 0px rgba(63,106,255,0.3);
  border-radius:8px;
  text-align: center;
  font-size: 8px;
  color: #3F6AFF;
  float: left;
  margin-left: 1%;
}
