/* devmanagement */
.top {
  width: 100%;
  height: 10%;
  padding-bottom: 1%;
}
.feny{
  float: right;
  margin-right:10px;
  margin-top:10px;
}
.baj{
  width:100%;
  min-height:100%;
  overflow: hidden;
  background:rgba(255,255,255,1);
  box-shadow:0px 0px 13px 0px rgba(63,106,255,0.08);
  border-radius:3px;
  padding:1%;
}
.btn{
  display: inline-block;
  padding: 1%;
  padding-top: 0.5%;
  padding-bottom:0.5%;
  background:linear-gradient(90deg,rgba(63,106,255,1) 0%,rgba(109,142,255,1) 100%);
  box-shadow:0px 0px 11px 0px rgba(63,106,255,0.28);
  border-radius:5vh;
  text-align: center;
  font-size: 1.2vh;
  color: #fff;
  margin-left: 1%;
}
.btn2{
  display: inline-block;
  padding: 1%;
  padding-top: 0.5%;
  padding-bottom:0.5%;
  background:#fff;
  border:1px solid rgba(63,106,255,1);
  box-shadow:0px 3px 8px 0px rgba(63,106,255,0.3);
  border-radius:5vh;
  text-align: center;
  font-size: 1.2vh;
  color: #3F6AFF;
  margin-left: 1%;
}
.updiv{
  width: 50%;
  height: 80%;
  position: fixed;
  background: #fff;
  left: 25%;
  top: 15%;
  z-index: 100;
  border: 1px solid #ccc;
}
.updiv_header{
  width: 100%;
  height: 30px;
  border-bottom: 1px solid #ccc;
}
.updiv_biao{
  font-size: 15px;
  line-height: 30px;
  font-weight: 800;
  margin-left: 15px;
}
.updiv_cha{
  float: right;
  font-size: 30px;
  margin-right: 15px;
}
.updiv_left{
  height: calc(100% - 30px);
  float: left;
  width: 49%;
  padding: 5px;
}
.updiv_right{
  height: calc(100% - 30px);
  width: 49%;
  float: right;
  padding: 5px;
}
