result:
.glasys-container{
  color: #1D2133;

  .bg{
    background-color: #ffffff;
    border-radius: 6px;
    padding: 30px;
    height: 100%;
  }

  p{
    margin: 0;
  }

  .title{
    font-size: 16px;
    font-weight: 550;
  }

  .lable{
    font-size: 14px;
    font-weight: 550;
  }

  .p-btn{
    text-decoration: underline;
    color: rgb(63, 106, 255);
    cursor: pointer;
  }

  .f-l{
    float: left;
  }

  .f-r{
    float: right;
  }

  .date-picker{
    width: 100%;
    margin-left: 0px;
  }

  .company-info-btn{
    margin-left: 20px;
    margin-top: -8px;
  }

  .first-row{
    height: 220px;

    .license-time{
      background-color: #E7EDFF;
      color: #3F6AFF;
      height: 40px;
      line-height: 40px;
      margin-right: -30px;
      margin-top: -11px;
      position: relative;
      padding: 0 8px 0 40px;

      &::before{
        content: '';
        position: absolute;
        left: -20px;
        background-color: #fff;
        width: 40px;
        height: 40px;
        transform: rotate(-45deg);
      }

      div{
        display: inline-block;
        margin: 0 10px;
      }
    }

    .company-info-item{
      margin-top: 20px;

      p{
        height: 32px;
        line-height: 32px;
      }

      .email{
        text-indent: 28px;
      }

      .info-lable{
        text-indent: 14px;
      }
    }

    .review-item{
      padding: 5% 10% 5% 5%;

      .review-step{
        padding: 0 20px;
        margin-top: 10px;
        display: flex;

        & div{
          display: inline-block;

          img{
            height: 26px;
            width: 24px;
          }
        }

        .step-line{
          width: calc(100% - 112px);
          padding: 5px;

          & div{
            width: calc(50% - 2px);
            height: 2px;
            border-bottom: 2px dotted #5DD4AC;
            display: inline-block;
          }
        }
      }

      .step-btn{
        margin-top: 15px;
        display: flex;

        & div{
          width: 70px;
          text-align: center;
          position: relative;

          p{
            height: 30px;
          }
        }

        & div:nth-child(2){
          margin: 0 auto;
        }

        .review-status{
          position: absolute;
          font-size: 12px;
          right: -66px;
          top: -3px;
          border: 1px solid #5DD4AC;
          color: #5DD4AC;
          text-align: center;
          height: 20px;
          line-height: 20px;
          border-radius: 2px;
        }

        .review-pending{
          border-color: #C9CBD7;
          color: #C9CBD7;
        }

        .review-return{
          border-color: #FE706C;
          color: #FE706C;
        }
      }
    }
  }

  .second-row{
    margin-top: 15px;
    height: calc(100% - 235px);

    .user-wapper{
      height: 50%;
    }

    .user-table{
      margin-top: 10px;
      height: calc(100% - 32px);

      .user-el-table{
        height: 100%;
      }

      .modify-btn{
        width: 100%;

        p{
          float: left;
          margin-right: 5px;
        }
      }
    }

    .role-wapper{
      margin-top: 15px;
      height: calc(50% - 15px);
    }

    .auth-wapper{
      height: calc(100% - 30px);
      position: relative;

      .auth-edit-btn{
        position: absolute;
        left: 0;
        right: 0;
        bottom: 5px;
        margin: auto;
      }
    }
  }

  .company-title p:nth-child(3),
  .company-title p:nth-child(2){
    margin-left: 19px;
  }

  .company-tree{
    height: calc(100% - 20px);
    margin-top: 40px;
    overflow-y: auto;
  }

  .add-company-dlg{
    width:90%;
    margin: auto;

    .el-form-item{
      margin-bottom: 20px;
    }
  }

  .dialog-footer{
    width: 100%;
    display: flex;
    justify-content: space-around;
    padding: 0 15% 15px;
  }
}
