result:
  @import "~@/styles/mixin.scss";
  @import "~@/styles/variables.scss";

  .app-wrapper {
    @include clearfix;
    position: relative;
    height: 100%;
    width: 100%;

    &.mobile.openSidebar {
      position: fixed;
      top: 0;
    }
  }

  .drawer-bg {
    background: #000;
    opacity: 0.3;
    width: 100%;
    top: 0;
    height: 100%;
    position: absolute;
    z-index: 999;
  }

  .fixed-header {
    position: fixed;
    top: 0;
    right: 0;
    z-index: 9;
    width: calc(100% - #{$sideBarWidth});
    transition: width 0.28s;
  }

  .hideSidebar .fixed-header {
    width: calc(100% - 54px);
  }

  .mobile .fixed-header {
    width: 100%;
  }

  .breadcrumb-container{
    position: fixed;
    left: 54px;
    top: 50px;
    height: 30px;
    line-height: 30px !important;
    margin-left: 0 !important;
    font-size: 13px !important;
    padding-left: 18px;
    z-index: 998;
    background-color: #fff;
    width: calc(100vw - 72px);
  }

.alarm-right{
  position: fixed;
  right: 0;
  bottom: 80px;
  width: 101px;
  height: 66px;
  padding-left: 58px;
  line-height: 56px;
  color: #ffffff;
  z-index: 1002;
}
